// Tab2 Page Changed

function tabTab2_OnChanged(obj,nOldIndex,nNewindex)

{

	if(nNewindex = 1) {

		btnEduPrtH.Visible = true;

		btnEducationInfo.Visible = false;

		if(Div3.Visible == true) {

			Div3.Visible = false;

			Div3.Top = 595;

		}

	} else {

		btnEduPrtH.Visible = false;

		btnEducationInfo.Visible = true;

	}

}



