// 대표직능 변경

function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{		

	if(left(strCode, 1) == "1") {	//방화관리자

	

		lfn_AreaNBizHidden(true, false);	//제연설비	DIV & 사업자등록번호 DIV

		lfn_BusinessHidden(true);			//용도 히든

		lfn_AgencyHidden(true);				//업무대행 보임

		lfn_CustomerStatus(true);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

		

	} else if(left(strCode, 1) == "3") {	//위험물안전관리자

	

		lfn_AreaNBizHidden(false, false);	//제연설비	DIV & 사업자등록번호 DIV

		lbSection.Text = "인력구분";

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(true);				//업무대행 보임

		lfn_CustomerStatus(true);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

				

	} else if(left(strCode, 1) == "5") {	//소방기술자

	

		lfn_AreaNBizHidden(false, true);	//제연설비	DIV & 사업자등록번호 DIV

		lbSection.Text = "인력구분";

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(false);			//업무대행 보임

		lfn_CustomerStatus(true);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

		

	} else if(left(strCode, 1) == "6") {	//개인회원

	

		lfn_AreaNBizHidden(false, false);	//제연설비	DIV & 사업자등록번호 DIV

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(false);			//업무대행 히든

		lfn_CustomerStatus(true);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(true);		//개인회원 히든처리

		

	} else if(left(strCode, 1) == "7") {	//단체회원

	

		lfn_AreaNBizHidden(false, false);	//제연설비	DIV & 사업자등록번호 DIV

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(false);			//업무대행 히든

		lfn_CustomerStatus(true);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

		

	} else if(left(strCode, 1) == "8") {	//위험물운송자

	

		lfn_AreaNBizHidden(false, false);	//제연설비	DIV & 사업자등록번호 DIV

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(false);			//업무대행 히든

		lfn_CustomerStatus(false);			//고객상태 비활성화

		lfn_AddressHidden(false);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

		

	} else if(left(strCode, 1) == "9") {	//교육희망자

	

		lfn_AreaNBizHidden(false, false);	//제연설비	DIV & 사업자등록번호 DIV

		lfn_BusinessHidden(false);			//용도 히든

		lfn_AgencyHidden(false);			//업무대행 보임

		lfn_CustomerStatus(false);			//고객상태 활성화

		lfn_AddressHidden(true);			//대표주소 입력 히든처리

		lfn_PersonCustomerHidden(false);	//개인회원 히든처리

	}

	

	if(ds_oAllObject.GetColumn(0, "BMGNO") == 1) {		//직능에 관계없이 

        lfn_BuildingInfoStatusControl(false);	// bmgno 가 1일 경우 비활성화함

	} 

		

	lfn_FilterPersonGubun(strCode);			// 인력 구분 필터링

	lfn_FilterDetailCourse(strCode);		// 세부고객직능 필터링

	lfn_BuildingAddInfoHidden();			// 대상물 부가정보 히든처리

	lfn_InitComboBoxIndex(1);				// 대상물 콤보 index=0으로 초기화

}



/* 2010-01-18 정현호 대리 추가 요청 START */

// 개인회원 히든처리 (개인회원)

function lfn_PersonCustomerHidden(bStatus) {

	//히든처리

	lbPersonCustomer.Visible = bStatus;		//안내문

	

	//대상물정보 조회(안전원)

	if(bStatus == true) {

		if(ds_ioCustomerInfo.GetRowType(0) == "insert") {

			lfn_searchCust0010BuildingInfo(1);

		}

		lfn_BuildingInfoStatus(false);

	} else {	//개인회원이 아닐때

		if(ds_ioCustomerInfo.GetRowType(0) <> "insert") {

		

		} else {

			ds_ioBuildingInfo.ClearData();

			ds_ioBuildingInfo.InsertRow(0);

			radAddrGubun.Index = 0;		//대상물 주소(구주소)

			lfn_BuildingInfoStatus(true);

		}

	}

}

/* 2010-01-18 정현호 대리 추가 요청 END */



// 대표주소 입력 히든처리 (위험물운송자)

function lfn_AddressHidden(bStatus) {

	// 히든처리

	// 대상물 주소

	lbDelegateAddr.Visible = bStatus;

	lbOldAddr.Visible = bStatus;

	edOldAddr1.Visible = bStatus;

	edOldAddr2.Visible = bStatus;

	btnSearchOldZipCode.Visible = bStatus;

	medOldZipCode.Visible = bStatus;

	lbNewAddr.Visible = bStatus;

	edNewAddr1.Visible = bStatus;

	edNewAddr2.Visible = bStatus;

	btnSearchNewZipCode.Visible = bStatus;

	medNewZipCode.Visible = bStatus;

	radAddrGubun.Visible = bStatus;

	// 고객 주소

	cbxAddPost.Visible = bStatus;

	btnCustFee.Visible = false;	//bStatus;

	btnEduNoti.Visible = false;	//bStatus;

	Div4.Visible = bStatus;

	Div0.Visible = bStatus;

	

	// 데이터 초기화

	// 대상물 주소

	if( gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {

		if(bStatus == false) {

			edOldAddr1.Text = "";

			edOldAddr2.Text = "";

			medOldZipCode.Value = "";

			edNewAddr1.Text = "";

			edNewAddr2.Text = "";

			medNewZipCode.Value = "";

			radAddrGubun.Index = 0;

			// 고객 주소

			//공통(교육)

			Div4.edEduPost1.Text = "";

			Div4.edEduPost2.Text = "";

			Div4.medEduPostZipCode.Value = "";

			Div4.cbxEduPostGubun.Index = 0;

			Div4.edEduPostPlace.Text = "";

			//회비

			Div0.edPost1.Text = "";

			Div0.edPost2.Text = "";

			Div0.medPostZipCode.Value = "";

			Div0.cbxPostGubun.Index = 0;

			Div0.edPostPlace.Text = "";

		}

	}

}



// 제연설비 & 업체구분 히든처리

function lfn_AreaNBizHidden(bStatus1, bStatus2) {

	div1.Visible = bStatus1;				//제연설비	DIV

	div2.Visible = bStatus2;				//사업자등록번호 DIV

	

	//데이타값 초기화

	if( gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {

		if(bStatus1 == false) {

			div1.cbxEquipmentYn.Value = "";

			div1.cbxEquipmentYn.Text = "";

			div1.medArea.Value = "";

		}

		if(bStatus2 == false) {

			div2.cbxBizGubun.Value = "";

			div2.cbxBizGubun.Text = "";

			div2.medCompanyNo.Value = "";

		}

	}

}



