	btnSearchAgency.Visible = bStatus;		//업무대행 팝업버튼

	

	//데이타값 초기화

	/*

	if(bStatus == false) {

		div1.cbxEquipmentYn.Value = "";

		div1.cbxEquipmentYn.Text = "";

		div1.medArea.Value = "";

	}

	*/

}



// 용도 히든처리

function lfn_BusinessHidden(bStatus) {

	lbBusiness.Visible = bStatus;			//용도

	cbxBusiness.Visible = bStatus;			//용도

	

	//데이타값 초기화

	if( gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {

		if(bStatus == false) {

			cbxBusiness.Value = "";

			cbxBusiness.Text = "";

		}

	}

}



// 고객상태 비활성화 처리

function lfn_CustomerStatus(bStatus) {

	cbxStatus.Enable = bStatus;				//고객상태

	medMemStatusDate.Enable = bStatus;		//고객상태변경처리일자

	cbxStatusReason.Enable = bStatus;		//고객상태변경사유

	

	//데이타값 초기화

	if(bStatus == false) {

		cbxStatus.Value = 1;

		medMemStatusDate.Text = "";

	}

}



// 세부직능 변경

function cbxDetailCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	if(strCode == "13" || strCode == "14") {	//공공기관방화관리자

		lbSection.Text = "공공선임";

	} else if(left(cbxCourse.Value,1) = "3") {

		lbSection.Text = "인력구분";

	} else if(left(cbxCourse.Value,1) = "5") {

		lbSection.Text = "인력구분";

	}

	

	lfn_FilterPersonGubun(strCode);

}



// 공통(교육통지) 우편물 선택

function Div4_cbxEduPostGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	lfn_EventDiv4CbxEduPostGubun(strCode);

}



// 회비고지서 우편물 선택 Event

function lfn_EventDiv4CbxEduPostGubun(strCode, bStatus) {

	var bMsgYn = true;

	if(!gfn_IsNull(bStatus)) bMsgYn = bStatus;

	

	if(cbxAddPost.Value == 0) {	//공통이면 회비와 교육 모두 적용

		switch(strCode)

		{

			case 0 :	//대상물

				Div0.edPostPlace.Text = Div4.edEduPostPlace.Text;

				Div0.cbxPostGubun.Value = 0;	//회비주소구분

				if(radAddrGubun.Value == 0) {	//구주소

						//회비

						Div0.edPost1.Text = edOldAddr1.Text; //주소1

						Div0.edPost2.Text = edOldAddr2.Text;	//주소2

						Div0.medPostZipCode.Value = medOldZipCode.Value;	//우편번호

						

						Div0.edPost1.Enable = false;

						Div0.edPost2.Enable = false;

						Div0.btnSearchPostZipCode.Enable = false;

						//공통(교육)

						Div4.edEduPost1.Text = edOldAddr1.Text; //주소1

						Div4.edEduPost2.Text = edOldAddr2.Text;	//주소2

						Div4.medEduPostZipCode.Value = medOldZipCode.Value;	//우편번호

						

						Div4.edEduPost1.Enable = false;

						Div4.edEduPost2.Enable = false;

						Div4.btnSearchEduPostZipCode.Enable = false;

						

					if(gfn_IsNull(edOldAddr1.Text) || gfn_IsNull(edOldAddr2.Text) || gfn_IsNull(medOldZipCode.Value)) {	//원본 주소가 Null일때

						if(bMsgYn == true) {

							gfn_Alert("대상물의 구주소에 빈값이 존재합니다.\n먼저 대상물의 구주소를 입력하세요.");

							edOldAddr1.SetFocus();	//대상물의 주소로 이동.

						}

						return;

					}

				} else if(radAddrGubun.Value == 1) {	//신주소

					gfn_Alert("대상물의 신주소를 복사합니다.");

				}

			break;

			case 1 :	//선임자

				Div0.edPostPlace.Text = Div4.edEduPostPlace.Text;

				Div0.cbxPostGubun.Value = 1;	//회비주소구분

				if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 0) {	//구주소

					//회비

					Div0.edPost1.Text = tabTab3.tabpgTab3Page1.edOldPostAddr1.Text;	//주소1

					Div0.edPost2.Text = tabTab3.tabpgTab3Page1.edOldPostAddr2.Text;	//주소2

					Div0.medPostZipCode.Value = tabTab3.tabpgTab3Page1.medOldPostZipCode.Value;	//우편번호

					

					Div0.edPost1.Enable = false;

					Div0.edPost2.Enable = false;

					Div0.btnSearchPostZipCode.Enable = false;

					//공통(교육)

					Div4.edEduPost1.Text = tabTab3.tabpgTab3Page1.edOldPostAddr1.Text;	//주소1

					Div4.edEduPost2.Text = tabTab3.tabpgTab3Page1.edOldPostAddr2.Text;	//주소2

					Div4.medEduPostZipCode.Value = tabTab3.tabpgTab3Page1.medOldPostZipCode.Value;	//우편번호

					

					Div4.edEduPost1.Enable = false;

					Div4.edEduPost2.Enable = false;

					Div4.btnSearchEduPostZipCode.Enable = false;

						

					if(gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr1.Text) 

					|| gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr2.Text) 

					|| gfn_IsNull(tabTab3.tabpgTab3Page1.medOldPostZipCode.Value)) {	//원본 주소가 Null일때

						if(bMsgYn == true) {

							gfn_Alert("선임자의 구주소에 빈값이 존재합니다.\n먼저 선임자의 구주소를 입력하세요.");

							tabTab3.tabpgTab3Page1.edOldPostAddr1.SetFocus();	//선임자 구주소로 이동.

						}

						return;

					}

				} else if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 1) {	//신주소

					gfn_Alert("선임자의 신주소를 복사합니다.");

				}

			break;

			case 2 :	//직접

				if(gfn_IsNull(Div4.medEduPostZipCode.Value)){

					Div0.edPostPlace.Text = Div4.edEduPostPlace.Text;

					Div0.cbxPostGubun.Value = 2;	//회비주소구분

					//회비

					Div0.edPost1.Text = "";	//주소1

					Div0.edPost2.Text = "";	//주소2

					Div0.medPostZipCode.Value = "";	//우편번호

					

					//공통(교육)

					Div4.edEduPost1.Text = "";	//주소1

					Div4.edEduPost2.Text = "";	//주소2

					Div4.medEduPostZipCode.Value = "";	//우편번호

				}				

				//회비

				Div0.edPost1.Enable = true;

				Div0.edPost2.Enable = true;

				Div0.btnSearchPostZipCode.Enable = true;

				

				//공통(교육)

				Div4.edEduPost1.Enable = true;

				Div4.edEduPost2.Enable = true;

				Div4.btnSearchEduPostZipCode.Enable = true;

			break;

		}

	} else {

		//교육통지만 변경

		switch(strCode)

		{

			case 0 :	//대상물

				if(radAddrGubun.Value == 0) {	//구주소

					Div4.edEduPost1.Text = edOldAddr1.Text; //주소1

					Div4.edEduPost2.Text = edOldAddr2.Text;	//주소2

					Div4.medEduPostZipCode.Value = medOldZipCode.Value;	//우편번호

					

					Div4.edEduPost1.Enable = false;

					Div4.edEduPost2.Enable = false;

					Div4.btnSearchEduPostZipCode.Enable = false;

					

					if(gfn_IsNull(edOldAddr1.Text) || gfn_IsNull(edOldAddr2.Text) || gfn_IsNull(medOldZipCode.Value)) {	//원본 주소가 Null일때

						if(bMsgYn == true) {

							gfn_Alert("대상물의 구주소에 빈값이 존재합니다.\n먼저 대상물의 구주소를 입력하세요.");

							edOldAddr1.SetFocus();	//대상물의 주소로 이동.

						}

						return;

					}

				} else if(radAddrGubun.Value == 1) {	//신주소

					gfn_Alert("대상물의 신주소를 복사합니다.");

				}

			break;

			case 1 :	//선임자

				if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 0) {	//구주소

					Div4.edEduPost1.Text = tabTab3.tabpgTab3Page1.edOldPostAddr1.Text;	//주소1

					Div4.edEduPost2.Text = tabTab3.tabpgTab3Page1.edOldPostAddr2.Text;	//주소2

					Div4.medEduPostZipCode.Value = tabTab3.tabpgTab3Page1.medOldPostZipCode.Value;	//우편번호

					

					Div4.edEduPost1.Enable = false;

					Div4.edEduPost2.Enable = false;

					Div4.btnSearchEduPostZipCode.Enable = false;

					

					if(gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr1.Text) 

					|| gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr2.Text) 

					|| gfn_IsNull(tabTab3.tabpgTab3Page1.medOldPostZipCode.Value)) {	//원본 주소가 Null일때

						if(bMsgYn == true) {

							gfn_Alert("선임자의 구주소에 빈값이 존재합니다.\n먼저 선임자의 구주소를 입력하세요.");

							tabTab3.tabpgTab3Page1.edOldPostAddr1.SetFocus();	//선임자 구주소로 이동.

						}

						return;

					}

				} else if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 1) {	//신주소

					gfn_Alert("선임자의 신주소를 복사합니다.");

				}

			break;

			case 2 :	//직접

				Div4.edEduPost1.Text = "";	//주소1

				Div4.edEduPost2.Text = "";	//주소2

				Div4.medEduPostZipCode.Value = "";	//우편번호

				

				Div4.edEduPost1.Enable = true;

				Div4.edEduPost2.Enable = true;

				Div4.btnSearchEduPostZipCode.Enable = true;

			break;

		}

	}

}



// 회비고지서 우편물 선택

function Div0_cbxPostGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	lfn_EventDiv0CbxPostGubun(strCode);

}



// 회비고지서 우편물 선택

function lfn_EventDiv0CbxPostGubun(strCode) {

	switch(strCode)

	{

		case 0 :	//대상물

			if(radAddrGubun.Value == 0) {	//구주소

				Div0.edPost1.Text = edOldAddr1.Text; 	//주소1

				Div0.edPost2.Text = edOldAddr2.Text;	//주소2

				Div0.medPostZipCode.Value = medOldZipCode.Value;	//우편번호

				

				Div0.edPost1.Enable = false;

				Div0.edPost2.Enable = false;

				Div0.btnSearchPostZipCode.Enable = false;

				

				if(gfn_IsNull(edOldAddr1.Text) || gfn_IsNull(edOldAddr2.Text) || gfn_IsNull(medOldZipCode.Value)) {	//원본 주소가 Null일때

					gfn_Alert("대상물의 구주소에 빈값이 존재합니다.\n먼저 대상물의 구주소를 입력하세요.");

					edOldAddr1.SetFocus();	//대상물의 주소로 이동.

					return;

				}

			} else if(radAddrGubun.Value == 1) {	//신주소

				gfn_Alert("대상물의 신주소를 복사합니다.");

			}

		break;

		case 1 :	//선임자

			if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 0) {	//구주소

				Div0.edPost1.Text = tabTab3.tabpgTab3Page1.edOldPostAddr1.Text;	//주소1

				Div0.edPost2.Text = tabTab3.tabpgTab3Page1.edOldPostAddr2.Text;	//주소2

				Div0.medPostZipCode.Value = tabTab3.tabpgTab3Page1.medOldPostZipCode.Value;	//우편번호

				

				Div0.edPost1.Enable = false;

				Div0.edPost2.Enable = false;

				Div0.btnSearchPostZipCode.Enable = false;

				

				if(gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr1.Text) 

				|| gfn_IsNull(tabTab3.tabpgTab3Page1.edOldPostAddr2.Text) 

				|| gfn_IsNull(tabTab3.tabpgTab3Page1.medOldPostZipCode.Value)) {	//원본 주소가 Null일때

					gfn_Alert("선임자의 구주소에 빈값이 존재합니다.\n먼저 선임자의 구주소를 입력하세요.");

					return;

				}

			} else if(tabTab3.tabpgTab3Page1.radPostAddrGubun.Value == 1) {	//신주소

				gfn_Alert("선임자의 신주소를 복사합니다.");

			}

		break;

		case 2 :	//직접

			Div0.edPost1.Text = "";	//주소1

			Div0.edPost2.Text = "";	//주소2

			Div0.medPostZipCode.Value = "";	//우편번호

			

			Div0.edPost1.Enable = true;

			Div0.edPost2.Enable = true;

			Div0.btnSearchPostZipCode.Enable = true;

		break;

	}

}







// 희망주소 공통 처리

function Div4_OnKillFocus(obj)

{

	if(cbxAddPost.Value == 0) {	//공통

		Div0.edPost1.Text = Div4.edEduPost1.Text;					//주소1

		Div0.edPost2.Text = Div4.edEduPost2.Text;					//주소2

		

		Div0.medPostZipCode.Value = Div4.medEduPostZipCode.Value;	//우편번호

		Div0.edPostPlace.Text = Div4.edEduPostPlace.Text;			//투입장소

		Div0.cbxPostGubun.Value = Div4.cbxEduPostGubun.Value;		//희망주소구분

		

		if(Div4.cbxEduPostGubun.Value == 2) {	//공통이 직접이면

			Div0.edPost1.Enable = true;

			Div0.edPost2.Enable = true;

			Div0.btnSearchPostZipCode.Enable = true;

		} else {	//대상물, 선임자

			Div0.edPost1.Enable = false;

			Div0.edPost2.Enable = false;

			Div0.btnSearchPostZipCode.Enable = false;

		}

	}

}



// 희망주소 공통 처리

function cbxAddPost_OnKillFocus(obj)

{

	Div4_OnKillFocus();

}



// 소속/타지부 선택

function radJibuGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

	if (strCode == "S") { // 소속지부

        cbxJibu.Enable = false;

        radJibuGubun.BkColor = "";

        if (ds_oJibu.FindRow("CD", gds_oUserInfo.GetColumn(0, "PDEPTCD")) <> -1) {

			if(gds_oUserInfo.GetColumn(0, "PDEPTCD") <> cbxJibu.Value) {

				cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");

				lfn_JibuChange(cbxJibu.Value);	//지부변경

			}

        }

	} else { // 타지부

        cbxJibu.Enable = true;

        radJibuGubun.BkColor = "red";

	}

}



// 지부변경

function lfn_JibuChange(strCode) {

	lfn_ClearSearchCompornent();				// 상단 조회 클리어

	gfn_SearchCodeRegion(strCode, ds_oRegion, "0");	//지역 코드 조회

	

	lfn_FilterFireStation(strCode);				//소방서 필터링

	lfn_ClearData();							//초기화 (모든 데이터셋)

	lfn_AddDataMain();							//Main 데이타셋 입력가능상태로 만들기

}



// 추가버튼 선택시 초기화

function lfn_InputDataSetting(obj) {

	medCustNo.Value = "";						//고객번호

	medBarCode.Value = "";						//고객바코드

    cbxCourse.Enable = true;					//대표직능

	

	/* 대상물 정보 */

	cbxStation_OnChanged(Object("cbxStation"), cbxStation.Value);					//대상물 소방관서 강제 이벤트

	btnSearchBuilding.Enable = true;			//대상물찾기 버튼 활성화

	cbxBusiness.Value = "01";					//용도 (01:근린생활시설)

	radAddrGubun.Value = 0;						//대상물 주소(0:구주소)

	div1.cbxEquipmentYn.Value = 1;				//제연설비(1:아니오)

	

	/* 대상물 부가정보 */

	lfn_BuildingAddInfoHidden();

	

	/* 회비/교육비 */

	medEmailCnt.Value = 0;						//총미납금액

	

	/* 고객정보 */

	meJoinYear.Value = left(GetDate(), 6);											//등록년월

	meFeeYear.Value = left(GetDate(), 6);											//회비부과년월

	cbxStatus.Value = 1;															//회원상태(0:회원,1:비회원)

	cbxAddPost.Value = 0;															//우편물청구구분(0:공통청구,1:개별청구)

	div0.cbxPostGubun.Value = 0;													//회비희망주소지(0:대상물,1:선임자,2:직접)

	div4.cbxEduPostGubun.Value = 0;													//교육희망주소지(0:대상물,1:선임자,2:직접)

	cbxAddPost_OnChanged(object("cbxAddPost"), cbxAddPost.Value);					//우편물청구구분 Event 강제 발생 시킴

	cbxDetailCourse_OnChanged(object("cbxDetailCourse"), cbxDetailCourse.Value);	//인력구분 Event 강제 발생 시킴

	cbxExclusionYN.Value = 1;														//관리제외(아니오)

	cbxAgencyYn.Value = 1;															//업무대행(미대행)

	btnSaveAddInfo.Enable = false;													//부가정보(비활성화)

	

	/* 선임자 정보 */

	edManagerNm.Readonly = false;													//성명(활성화)

	medResidentNo.Readonly = false;													//주민번호(활성화)

	lbResidentNoError.Visible = false;												//오류(비활성화)

	calAppointDate.Value = left(GetDate(), 8);										//선임일

	btnUpdateManagerInfo.Enable = false;											//선임자 정보변경(비활성화)

	tabTab3.tabpgTab3Page1.radPostAddrGubun.Value = 0;								//선임자 주소(구주소)

	

	cbxRegion.SetFocus();						//지역 콤보 포커스

}



