// 동일 대상물 체크

function lfn_searchSameBuildingCheck(obj) {

	

	var sBnm = lfn_ReplaceBnm(trim(edBuildingNm.Text));  // 검색대상물명

	

	ds_oCount.ClearData(); // 데이타셋 초기화



    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010BuildingCountCheck", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_oCount=ds_oCount" // outDs

        , "SBNM=" + quote(sBnm) 	// 대상물명

        + " SJIBU=" + quote(trim(cbxJibu.Value)) 	// 지부

        , "lfn_AfterSearchSameBuildingCheck"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : 동일 대상물 체크 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchSameBuildingCheck(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    if ( ds_oCount.GetRowCount() > 0) {

		if(toNumber(ds_oCount.GetColumn(0, "CNT")) > 0 ) {

			//gfn_Alert("동일한 대상물명이 존재합니다.");

			btnSearchBuilding_OnClick();

		}

    }

}





