/////////////////DELETE///////////////////

/*

 * 기능 : 관계자 부가정보 삭제

 * parameter : 없음

 * Return : 없음

 */

function lfn_DeleteOwnerInfo(Mgno, Seq) {



	if(gfn_IsNull(Mgno) || gfn_IsNull(Seq)) {

		return;

	}

	

	lfn_CustomerModifyHistoryGeneratorDelete(ds_ioOwnerInfo, ds_ioOwnerInfo.currow);	//변경이력 수집(삭제)

	

	tit_clearActionInfo();

	tit_addSingleActionInfo("cust:deleteCust0010OwnerInfo", "", 0, "");

	tit_addMultiActionInfo("cust:insertCust0010CustomerModifyHistory", "", 0, "", "N");



	// 서버 호출 

	tit_callService(

		""

		,""

		,"ds_iCustomerModifyHistory=ds_iCustomerModifyHistory:U"	// inDs

		,""	// outDs

		,"OBMGNO=" + quote(Mgno)

		+ " OSEQ=" + quote(Seq)

		,"lfn_AfterDeleteOwnerInfo"

		,false

		,false

		,true

	);

}



/*

 * 기능 : 관계자 부가정보 삭제 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterDeleteOwnerInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    ds_ioOwnerInfo.DeleteRow(ds_ioOwnerInfo.currow);

	gfn_Alert("정상적으로 삭제되었습니다.");	

}



/*

 * 기능 : 위험물 부가정보 삭제

 * parameter : 없음

 * Return : 없음

 */

function lfn_DeleteDangerInfo(Mgno, Seq) {

	

	if(gfn_IsNull(Mgno) || gfn_IsNull(Seq)) {

		return;

	}	

	

	lfn_CustomerModifyHistoryGeneratorDelete(ds_ioDangerInfo, ds_ioDangerInfo.currow);	//변경이력 수집(삭제)

	

	tit_clearActionInfo();

	tit_addSingleActionInfo("cust:deleteCust0010DangerInfo", "", 0, "");

	tit_addMultiActionInfo("cust:insertCust0010CustomerModifyHistory", "", 0, "", "N");



	// 서버 호출 

	tit_callService(

		""

		,""

		,"ds_iCustomerModifyHistory=ds_iCustomerModifyHistory:U"	// inDs

		,""	// outDs

		,"DBMGNO=" + quote(Mgno)

		+ " DSEQ=" + quote(Seq)

		,"lfn_AfterDeleteDangerInfo"

		,false

		,false

		,true

	);

}



/*

 * 기능 : 위험물 부가정보 삭제 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterDeleteDangerInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    ds_ioDangerInfo.DeleteRow(ds_ioDangerInfo.currow);

	gfn_Alert("정상적으로 삭제되었습니다.");	

}



