//사업자번호 체크

function div2_medCompanyNo_OnKillFocus(obj)

{

	var bCheck = lfn_CompanyNoCheck(trim(div2.medCompanyNo.Value));

	

	if(bCheck == false) {	//실패

		if (!gfn_Confirm("사업자등록번호가 형식에 맞지않습니다.\n강제로 저장 하시겠습니까?")) {

            div2.medCompanyNo.SetFocus();

            return false;

        }

	}

}



/*

 * 기능 : 사업자등록번호 체크

 * parameter : 사업자등록번호

 * return : 성공/실패 (true/false)

 */

function lfn_CompanyNoCheck(sCompanyNo) {

	var sum = 0;

    var getlist = Array(10);

    var chkvalue = Array(9);

		chkvalue[0] = "1";

		chkvalue[1] = "3";

		chkvalue[2] = "7";

		chkvalue[3] = "1";

		chkvalue[4] = "3";

		chkvalue[5] = "7";

		chkvalue[6] = "1";

		chkvalue[7] = "3";

		chkvalue[8] = "5";



	for(var i=0; i<10; i++)

	{

		//getlist[i] = vencod.substring(i, i+1);

		getlist[i] = substr(sCompanyNo, i, i+1);

	}



	for(var i=0; i<9; i++)

	{

		//sum += getlist[i]*chkvalue[i];

		sum += toNumber(getlist[i]) * toNumber(chkvalue[i]);

	}



	//sum = sum + parseInt((getlist[8]*5)/10);

	sum = toNumber(sum) + floor(parseInt((toNumber(getlist[8])*5)/10));

	

	sidliy = toNumber(sum) % 10;

	sidchk = 0;



	if(sidliy <> 0)

	{

		sidchk = 10 - sidliy;

	}

	else

	{

		sidchk = 0;

	}



	if(sidchk <> getlist[9])

	{

		return false;

	}

	return true;

}



//이동탱크부가정보 상세보기 팝업

function Div7_tabTab1_tabpgTab1Page3_gdTankInfo_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)

{

	if(nCell == 4) {

		//이동탱크 부가정보

		var args  = "sRow="+quote(nRow);

		if (ds_ioCustomerInfo.GetRowType(0) <> "insert" 

            && ds_ioCustomerInfo.GetColumn(0, "CDELGUBUN") <> "0") {

            args += " sDelGubun=Y";

            Dialog(ls_Prefix + "::frmcust0010PTankInfo.xml", args, 500, 308, "", -1, -1);

        } else {

            Dialog(ls_Prefix + "::frmcust0010PTankInfo.xml", args, 500, 308, "", -1, -1);

        }

	}

}



/*

 * 기능 : 검색을 위한 대상물명 변환

 * parameter : 대상물명

 * return : 대상물명(BSEARCHNM)

 */

function lfn_ReplaceBnm(sBnm) {

    var sBSEARCHNM = sBnm;

    ds_oBnmCode.ClearData();

    

    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010BnmReplaceCode", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_oBnmCode=ds_oBnmCode" // outDs

        , ""

        , ""

        ,false

        ,false

        ,true

    );

    

    for (i=0;i<ds_oBnmCode.GetCount();i++) {

        sBSEARCHNM = Replace(sBSEARCHNM, ds_oBnmCode.GetColumn(i, "CCCDNM"), "");

    }

    

    return sBSEARCHNM;

}



/*

 * 기능 : 저장 루틴

 * return : 없음

 */

function lfn_SaveCaseCombination() {

	

	if(ds_ioCustomerInfo.GetOrgColumn(0, "CREGCD") <> trim(cbxRegion.Value)) {

		ds_ioCustomerInfo.SetColumn(0, "CREGCD", trim(cbxRegion.Value));		//지역

	}

	

	if(ds_ioCustomerInfo.GetOrgColumn(0, "CCOURSECD") <> trim(cbxDetailCourse.Value)) {

		ds_ioCustomerInfo.SetColumn(0, "CCOURSECD", trim(cbxDetailCourse.Value));	//직능

	}

	

	var BSEARCHNM = lfn_ReplaceBnm(ds_ioBuildingInfo.GetColumn(0, "BNM"));  // 검색대상물명

		

	/* MAIN */

	var lsBuildStatus = ds_ioBuildingInfo.GetRowType(0);

	var lsCustomerStatus = ds_ioCustomerInfo.GetRowType(0);

	var lsManagerStatus = ds_ioManagerInfo.GetRowType(0);

		

	/* tit_SaveAction Start */

	ds_oObjectMgno.ClearData();	//고객정보관리번호

	

	tit_clearActionInfo();

	

	var lsGubun = " ";

	

	/* Dataset List */

	var iDsList = "";	//input Dataset List

	var oDsList = "";	//output Dataset List

	

	/* IN : MAIN */

	var iBuildingDsNm = "ds_ioBuildingInfo=ds_ioBuildingInfo:U";

	var iCustomerDsNm = "ds_ioCustomerInfo=ds_ioCustomerInfo:U";

	var iManagerDsNm  = "ds_ioManagerInfo=ds_ioManagerInfo:U";

	/* IN : 대상물 부가정보 */

	var iOwnerDsNm  = "ds_ioOwnerInfo=ds_ioOwnerInfo:U";

	var iDangerDsNm = "ds_ioDangerInfo=ds_ioDangerInfo:U";

	var iTankDsNm   = "ds_ioTankInfo=ds_ioTankInfo:U";

	/* IN : 고객 부가정보 */

	var iAdditionDsNm = "ds_ioAdditionInfo=ds_ioAdditionInfo:U";

	/* IN : 선임자 부가정보 */

	var iLicenseDsNm = "ds_ioLicenseInfo=ds_ioLicenseInfo:U";

	

	/* OUT : 관리번호 정보 */

	var oObjectMgnoNm = "ds_oObjectMgno=ds_oObjectMgno";

	var oSPMainIN = "ds_oSPMainIN=ds_oSPMainIN";

	

	var iDsListBuildingDsNm 		= iBuildingDsNm;

	var iDsListGubunBuildingDsNm 	= " " + iBuildingDsNm;

	var iDsListiCustomerDsNm 		= iCustomerDsNm;

	var iDsListGubunCustomerDsNm 	= " " + iCustomerDsNm;

	var iDsListiManagerDsNm 		= iManagerDsNm;

	var iDsListGubunManagerDsNm 	= " " + iManagerDsNm;

	var iDsListiOwnerDsNm 			= iOwnerDsNm;

	var iDsListGubunOwnerDsNm 		= " " + iOwnerDsNm;

	var iDsListiDangerDsNm 			= iDangerDsNm;

	var iDsListGubunDangerDsNm 	    = " " + iDangerDsNm;

	var iDsListiTankDsNm 			= iTankDsNm;

	var iDsListGubunTankDsNm 		= " " + iTankDsNm;

	var iDsListiAdditionDsNm 		= iAdditionDsNm;

	var iDsListGubunAdditionDsNm 	= " " + iAdditionDsNm;

	var iDsListiLicenseDsNm 		= iLicenseDsNm;

	var iDsListGubunLicenseDsNm 	= " " + iLicenseDsNm;

		

	//input Arguments List

	var iArgsNm = "";	

	/* 공통 */

		iArgsNm += "SJIBU=" + quote(cbxJibu.Value);				// 지부

		iArgsNm += " SREGION=" + quote(cbxRegion.Value);		// 지역

		iArgsNm += " COURSE=" + quote(cbxCourse.Value);			// 대표직능
		
		iArgsNm += " SCOURSE=" + quote(cbxDetailCourse.Value);	// 세부직능

		iArgsNm += " SBNM=" + quote(edBuildingNm.Text);			// 대상물명

		iArgsNm += " BSEARCHNM=" + quote(BSEARCHNM);			// 검색대상물명

		iArgsNm += " SCSTATUS=" + quote(cbxStatus.Value);		// 회원구분(0:회원,1:비회원)	♣♣♣♣♣♣♣♣♣♣♣♣

	/* INSERT 일때 사용 */

	var iArgsNmBuildingInsert = " SBMGNO="  + quote(ds_oBuildingSeq.GetColumn(0, "SEQ"));		// 대상물관리번호

	var iArgsNmCustomerInsert = " SCMGNO="  + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));	    // 고객관리번호

	var iArgsNmManagerInsert  = " SFMMGNO=" + quote(ds_oFireManagerSeq.GetColumn(0, "SEQ"));	// 선임자관리번호

	/* UPDATE 일때 사용 */

	var iArgsNmBuildingUpdate = " SBMGNO="  + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO"));	// 대상물관리번호

	var iArgsNmCustomerUpdate = " SCMGNO="  + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));	// 고객관리번호

	var iArgsNmManagerUpdate  = " SFMMGNO=" + quote(ds_ioManagerInfo.GetColumn(0, "FMMGNO"));		// 선임자관리번호

	

	//iArgsNm += iArgsNmBuildingUpdate;

	//iArgsNm += iArgsNmCustomerUpdate;

	//iArgsNm += iArgsNmManagerUpdate;

	

	//대상물

	if(lsBuildStatus == "insert") {

		lfn_SearchCust0010BuildingSeq();        // 대상물관리번호

		iDsList += iDsListBuildingDsNm;

		iArgsNm += " SBMGNO="  + quote(ds_oBuildingSeq.GetColumn(0, "SEQ"));

		

			//고객

			if(lsCustomerStatus == "insert") {

				lfn_SearchCust0010CustomerSeq();        // 고객관리번호

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += " SCMGNO="  + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));



					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010BuildingInfo", "", 0, "");					//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");					//고객

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");				//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));					// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호



					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010BuildingInfo", "", 0, "");					//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");					//고객

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");					//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");				//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

						

						if(!gfn_IsNull(ds_ioAdditionInfo.GetColumn(0, "ADCD"))) {

							tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");				//고객부가정보

							iDsList += iDsListGubunAdditionDsNm;

						}

					} else if(lsManagerStatus == "normal") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010BuildingInfo", "", 0, "");					//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");					//고객

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

						

						if(!gfn_IsNull(ds_ioAdditionInfo.GetColumn(0, "ADCD"))) {

							tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");				//고객부가정보

							iDsList += iDsListGubunAdditionDsNm;

						}

					}

					

			} else if(lsCustomerStatus == "update") {

			

				gfn_Alert("경우의수 없음.");

				return;

			} else if(lsCustomerStatus == "normal") {

			

				gfn_Alert("경우의수 없음.");

				return;

			}

			

	} else if(lsBuildStatus == "update") {

		iDsList += iDsListBuildingDsNm;

		iArgsNm += iArgsNmBuildingUpdate;

		

			//고객

			if(lsCustomerStatus == "insert") {

				lfn_SearchCust0010CustomerSeq();        // 고객관리번호

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += " SCMGNO="  + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");			//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");			//고객

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}



						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");			//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));					// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");		//고객	

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");		//고객	

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					}

				

			} else if(lsCustomerStatus == "update") {

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += iArgsNmCustomerUpdate;

				

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");		//고객	

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");		//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));				// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");		//고객	

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");		//고객	

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					}

					

			} else if(lsCustomerStatus == "normal") {

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += iArgsNmCustomerUpdate;

				

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");		//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));				// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010BuildingInfo", "", 0, "");		//대상물

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					}

			}

	} else if(lsBuildStatus == "normal") {

		iDsList += iDsListBuildingDsNm;

		iArgsNm += iArgsNmBuildingUpdate;

			//고객

			if(lsCustomerStatus == "insert") {

				lfn_SearchCust0010CustomerSeq();        // 고객관리번호

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += " SCMGNO="  + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));

				

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");		//고객	

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");		//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));					// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");		//고객	

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						//선임자는 처리없음.

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");		//고객	

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					}

				

			} else if(lsCustomerStatus == "update") {

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += iArgsNmCustomerUpdate;

		

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");			//고객

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");		//신규선임

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));				// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");		//고객

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						//선임자는 처리없음.

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010CustomerInfo", "", 0, "");		//고객

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

						

					}

			

			} else if(lsCustomerStatus == "normal") {

				iDsList += iDsListGubunCustomerDsNm;

				iArgsNm += iArgsNmCustomerUpdate;

					//선임자

					if(lsManagerStatus == "insert") {

						tit_clearActionInfo();

						

						if(left(cbxRegion.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외

							tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣

						}

						

						tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");				//신규선임 ♣♣♣♣♣♣

						iDsList += iDsListGubunManagerDsNm;

						

						iArgsNm += " I_CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));				// 고객정보관리번호

						iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자

						iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자

						iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호

						

					} else if(lsManagerStatus == "update") {

						tit_clearActionInfo();

						tit_addSingleActionInfo("cust:updateCust0010ManagerInfo", "", 0, "");		//선임자

						tit_addSingleActionInfo("cust:updateCust0010CustomerFMHAfter", "", 0, "");	//고객정보(선임자 이력후)

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

					

					} else if(lsManagerStatus == "normal") {

						//선임자는 처리없음.

						iDsList += iDsListGubunManagerDsNm;

						iArgsNm += iArgsNmManagerUpdate;

						

					}

			

			}

			

	}

	

	/* 부가정보 */

	/* 대상물관련 부가정보 */

	if (ds_ioOwnerInfo.GetUpdate() == true) {

		tit_addMultiActionInfo("cust:updateCust0010OwnerInfo", "", 0, "", "N");		//관계자

		iDsList += iDsListGubunOwnerDsNm;

	}

	if (ds_ioDangerInfo.GetUpdate() == true) {

		tit_addMultiActionInfo("cust:updateCust0010DangerInfo", "", 0, "", "N");	//위험물

		iDsList += iDsListGubunDangerDsNm;

	}

	if (ds_ioTankInfo.GetUpdate() == true) {

		tit_addMultiActionInfo("cust:updateCust0010TankInfo", "", 0, "", "N");		//이동탱크

		iDsList += iDsListGubunTankDsNm;

	}

	

	/* 고객 부가정보 */

	if (ds_ioAdditionInfo.GetUpdate() == true) {

		if(!gfn_IsNull(ds_ioAdditionInfo.GetColumn(0, "ADCD"))) {

			tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");		//부가정보

			iDsList += iDsListGubunAdditionDsNm;

		}

	}

	

	/* 선임자 부가정보 */

	if (ds_ioLicenseInfo.GetUpdate() == true) {

		if( lsManagerStatus == "insert" && gfn_IsNull(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMMGNO")) ) {	//선임자 신규등록

			tit_addMultiActionInfo("cust:insertCust0010OtherLicenseInfo", "", 0, "", "N");		//보유자격

			iDsList += iDsListGubunLicenseDsNm;

		} else {	//기존선임자

			tit_addMultiActionInfo("cust:updateCust0010OtherLicenseInfo", "", 0, "", "N");		//보유자격

			iDsList += iDsListGubunLicenseDsNm;

		}

	}

	

	//oDsList += oSPMainIN;

	

	if(lsCustomerStatus == "update" 

		&& !gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CFMHMGNO")) 

		&& !gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CFMHSEQ")) ) {	//고객정보가 수정상태이고 선임자정보가 있을경우

		/* 고객 정상에서 비정상으로(회원->비회원, 주민번호정상->주민번호오류)

		 *      0 : 변경없음

		 *      1 : 비회원		-> 회원

		 *      2 : 회원		-> 비회원

		 *      3 : 삭제		-> 정상

		 *      4 : 정상		-> 삭제

		 */

		var cFlag = lfn_CustomerStatusChanged();	//고객 변경 체크

		

		switch(cFlag)

		{

			case 0:

				//변경없음

			break;

			case 1:	//비회원 -> 회원

				iArgsNm += " SBADDR1=" + quote(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BADDR1"));

				oDsList += "ds_oOverBizProc=ds_oOverBizProc";

				tit_addSingleActionInfo("cust:executeCust0010CustStatusToRegularity", "", 0, "");		//비정상에서 정상으로

			break;

			case 2: //회원 -> 비회원

				tit_addSingleActionInfo("cust:executeCust0010CustStatusToIrregular", "", 0, "");		//정상에서 비정상으로

			break;

			case 3: //삭제 -> 정상

				iArgsNm += " SBADDR1=" + quote(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BADDR1"));

				oDsList += "ds_oOverBizProc=ds_oOverBizProc";

				tit_addSingleActionInfo("cust:executeCust0010CustStatusToRegularity", "", 0, "");		//비정상에서 정상으로

			break;

			case 4: //정상 -> 삭제

				tit_addSingleActionInfo("cust:executeCust0010CustomerDelete", "", 0, "");				//정상에서 비정상으로

			break;

		}

	}

	

	/* 2010-01-12 정현호 대리 추가 요청 START */

	// 대상물 주소 변경시 포함된 고객 주소 일괄변경(주소 구분이 대상물인 경우만)

	if(lsBuildStatus == "update") {

		if( ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1") <> ds_ioBuildingInfo.GetColumn(0, "BADDR1") 

		|| ds_ioBuildingInfo.GetOrgColumn(0, "BADDR2") <> ds_ioBuildingInfo.GetColumn(0, "BADDR2") 

		|| ds_ioBuildingInfo.GetOrgColumn(0, "BZIPCD") <> ds_ioBuildingInfo.GetColumn(0, "BZIPCD") ) {	//대상물 주소가 변경되었을때

			tit_addSingleActionInfo("cust:updateCust0010BuildingAddress", "", 0, "");

			

			/* 2010-01-25 정현호 대리 추가 요청 START */

			// 대상물 주소 변경시 주소1,성명,주민번호 가 동일한 경우 겸직이 안된 경우 겸직처리하기

			if(ds_ioNotOverBizFireManager.rowcount > 0) {

				iDsList += " ds_ioNotOverBizFireManager=ds_ioNotOverBizFireManager";

				tit_addMultiActionInfo("cust:executeCust0010OverBizProc", "", 0, "", "N");

			}

			// 대상물 주소 변경시 대상물관리번호가 다르나 주소1, 이름, 주민번호가 동일해서 겸직이 된 경우 겸직해임처리

			if(ds_ioIsOverBizFireManager.rowcount > 0) {

				iDsList += " ds_ioIsOverBizFireManager=ds_ioIsOverBizFireManager";

				tit_addMultiActionInfo("cust:executeCust0010OverBizFinshProc", "", 0, "", "N");

			}

			/* 2010-01-25 정현호 대리 추가 요청 END */

		}

	}

	/* 2010-01-12 정현호 대리 추가 요청 END */

	

	// 고객변경이력

	if(lsCustomerStatus == "insert") {



		tit_addSingleActionInfo("cust:executeCust0010InsertCustomerChangeH", "", 0, "");

	//} else if(lsCustomerStatus == "update" || (lsCustomerStatus == "normal" && lsManagerStatus == "insert") ) {	//고객변동 + (대상물+고객)에 선임자선임

	} else {	//고객변동 + (대상물+고객)에 선임자선임

		

		iDsList += " ds_iCustomerInfoTmp=ds_iCustomerInfoTmp";

		

		tit_addSingleActionInfo("cust:executeCust0010UpdateCustomerChangeH", "", 0, "");

	}

	

	//CustomerModifyHistory Insert

	if(lsCustomerStatus == "insert") {

		lfn_CustomerModifyHistoryGenerator(ds_oCustomerSeq.GetColumn(0, "SEQ"));	//고객정보변경History

	} else {

		lfn_CustomerModifyHistoryGenerator(ds_ioCustomerInfo.GetColumn(0, "CMGNO"));	//고객정보변경History

	}

	if(lsBuildStatus == "update" || lsCustomerStatus == "update" || lsManagerStatus == "update"

		|| ds_ioOwnerInfo.GetUpdate() == true || ds_ioDangerInfo.GetUpdate() == true

		|| ds_ioTankInfo.GetUpdate() == true || ds_ioLicenseInfo.GetUpdate() == true) {	

		iDsList += " ds_iCustomerModifyHistory=ds_iCustomerModifyHistory:U";

		tit_addMultiActionInfo("cust:insertCust0010CustomerModifyHistory", "", 0, "", "N");

	}

	

	//trace("iDsList :: "+iDsList);	//개발완료 후 삭제 요망

	//trace("oDsList :: "+oDsList);	//개발완료 후 삭제 요망

	//trace("iArgsNm :: "+iArgsNm);	//개발완료 후 삭제 요망

	

	// 서버 호출 

	tit_callService(

		""

		,""

		,iDsList	// inDs

		,oDsList	// outDs

		,iArgsNm	// args

		,"lfn_AfterSaveCaseCombination"

		,false

	);

}



/*

 * 기능 : 저장 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSaveCaseCombination(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    if(chkSameBuilding.Value == "Y") {	//동일대상물 계속입력

		var bMgno = ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BMGNO");

		lfn_Input();

		lfn_searchCust0010BuildingInfo(bMgno);

    } else {

		if(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO") > 0) {

			lfn_SaveCust0010AfterCustomerMgno(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));

			gfn_Alert("["+cbxJibu.Text+"지부 - "+ds_oObjectMgno.GetColumn(0,"CNO") + "("+ds_oObjectMgno.GetColumn(0,"CSTATUSGUBUN")+")] 정상적으로 처리되었습니다.");

			lfn_searchCust0010CustomerInfo(ds_oObjectMgno.GetColumn(0,"CGTMGNO"), ds_oObjectMgno.GetColumn(0,"CNO"));	//고객 정보 조회(전체 조회)

		} else {

			lfn_SaveCust0010AfterCustomerMgno(ds_oCustomerSeq.GetColumn(0, "SEQ"));

			gfn_Alert("["+cbxJibu.Text+"지부 - "+ds_oObjectMgno.GetColumn(0,"CNO") + "("+ds_oObjectMgno.GetColumn(0,"CSTATUSGUBUN")+")] 정상적으로 처리되었습니다.");

			lfn_searchCust0010CustomerInfo(ds_oObjectMgno.GetColumn(0,"CGTMGNO"), ds_oObjectMgno.GetColumn(0,"CNO"));	//고객 정보 조회(전체 조회)
			
		}

    }

}



/*

 * 기능 : 고객 변경 체크

 * parameter : 없음

 * Return : cFlag(상태변경구분)

 *      0 : 변경없음

 *      1 : 비회원		-> 회원

 *      2 : 회원		-> 비회원

 *      3 : 삭제		-> 정상

 *      4 : 정상		-> 삭제

 */

function lfn_CustomerStatusChanged() {



	var cFlag = 0;

	//고객상태 변경

	if(ds_ioCustomerInfo.GetOrgColumn(0, "CSTATUSGUBUN") <> ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN")) {	//회원구분 변경

		if(ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN") == 0) {	//비회원 -> 회원

			cFlag = 1;

		} else if(ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN") == 1) {	//회원 -> 비회원

			cFlag = 2;

		}

	}

	//상태구분 변경

	if(ds_ioCustomerInfo.GetOrgColumn(0, "CDELGUBUN") <> ds_ioCustomerInfo.GetColumn(0, "CDELGUBUN")) {	//회원구분 변경

		if(ds_ioCustomerInfo.GetColumn(0, "CDELGUBUN") == 0) {	//삭제 -> 정상

			cFlag = 3;

		} else {	//정상 -> 삭제

			cFlag = 4;

		}

	}

	return(cFlag);

}



// 소방서 콤보 Changed 이벤트

function cbxStation_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	lfn_FilterFireCenter(strCode);	// 소방센터 필터링

}



// 소방서 필터링

function lfn_FilterFireStation(sFilterGubun) {	

	ds_oFireCd.UnFilter();

		

	var ls_FilterExpr = "";

    if (!gfn_IsNull(sFilterGubun)) {

        ls_FilterExpr += "JIBUCD=='" + trim(sFilterGubun) + "'";

    }

    

    ds_oFireCd.Filter(ls_FilterExpr);

    

	if( ds_oFireCd.FindRow("DATA", cbxStation.Text) == -1) {

		cbxStation.Text = "";

    }

}



// 소방센터 필터링

function lfn_FilterFireCenter(sFilterGubun) {	

	ds_oFireCenterCd.UnFilter();

		

	var ls_FilterExpr = "";

    if (!gfn_IsNull(sFilterGubun)) {

        ls_FilterExpr += "UPCD=='" + trim(sFilterGubun) + "'";

    } else {

		ls_FilterExpr += "UPCD=='" + trim("~!@#$%") + "'";

    }

    

    ds_oFireCenterCd.Filter(ls_FilterExpr);

   

	if( ds_oFireCenterCd.FindRow("DATA", cbxCenter.Text) == -1) {

		cbxCenter.Text = "";

    }

}



