/* 그리드 Enter Key 이동 */

function lfn_GridEnterMove(obj, nChar, bShift) {

	if (nChar == 13) {

		if(bshift=1) {

			obj.MoveToPrevCell();

		} else { 

			obj.MoveToNextCell();

		}

	}

}



