/*

 * 기능 : 선임자 검색 팝업

 */

function btn_SearchFireManager_OnClick(obj)

{

	/*

     * Input >> sCmgno : 고객정보관리번호

     */

	var sArg  = "sManagerNm=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM")); 			// 선임자명

		sArg += " sResidentNo=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO")); 	// 선임자주민번호

		

	if( sNewFMGubun == 1 && !gfn_IsNull(ds_ioManagerInfo.GetColumn(0, "FMMGNO")) ) {	//기존상태

		sArg += " sFlag=" + quote(lbNewFMGubun.Text); 	// 기존상태

	}



    var arrManager = Dialog(ls_Prefix + "::frmcust0018PManagerList.xml", sArg, 600, 400, "", -1, -1);

    

    if(length(arrManager) > 0) {

		if(sNewFMGubun == 0 || (sNewFMGubun == 1 && gfn_IsNull(ds_ioManagerInfo.GetColumn(0, "FMMGNO"))) ) {		//신규

			if(arrManager[0] == "NEW") {

				//신규입력 가능상태

				ds_ioManagerInfo.ClearData();

				ds_ioManagerInfo.InsertRow(0);

				for(var i=0; i<ds_ioManagerInfo.GetColCount(); i++) {

					if(!(ds_ioManagerInfo.GetColID(i) <> "FMNM" || ds_ioManagerInfo.GetColID(i) <> "FMHSTARTDATE")) {

						ds_ioManagerInfo.SetColumn(0, ds_ioManagerInfo.GetColID(i), "");

					}

				}

				ds_ioManagerInfo.SetColumn(0, "FMNM", arrManager[1]);

				ds_ioManagerInfo.SetColumn(0, "FMRESIDENTNO", arrManager[2]);

				ds_ioManagerInfo.SetColumn(0, "FMHADDRGUBUN", 0);

				ds_ioManagerInfo.SetColumn(0, "FMHSTARTDATE", left(GetDate(),8));

				ds_ioOverBizInfo.ClearData();	//겸직정보

				ds_ioLicenseInfo.ClearData();	//자격정보(타자격)

				ds_oLicenseInfo.ClearData();	//자격정보(안전원자격)

			} else {

				lfn_SearchFireManaterInfo(arrManager[0], arrManager[1]);	//선임자 정보 검색

			}

			tabTab3.tabpgTab3Page1.edPTel.SetFocus();

		} else {	//기존(선임자변경 된 경우?)

			lfn_searchCust0010ManagerInfo();

		}

    }

}



/*

 * 기능 : 검색에 의한 선임자 정보

 */

function lfn_SearchFireManaterInfo(Mgno, Seq) {



	if(gfn_IsNull(Mgno) || gfn_IsNull(Seq)) {

		return;

	}

	

	ds_oManagerInfoTmp.ClearData(); // 데이타셋 초기화



    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010ManagerInfo", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_oManagerInfoTmp=ds_oManagerInfo" // outDs

        , "CFMHMGNO=" + quote(Mgno)

        + " CFMHSEQ=" + quote(Seq)

        , "lfn_AfterSearchFireManaterInfo"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : (검색에 의한 선임자 정보) 조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchFireManaterInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    if(ds_oManagerInfoTmp.GetRowCount() > 0) {

		ds_ioManagerInfo.ClearData();

		ds_ioManagerInfo.InsertRow(0);

		

		for(var i=0; i<ds_oManagerInfoTmp.GetColCount(); i++) {

			ds_ioManagerInfo.SetColumn(0, ds_oManagerInfoTmp.GetColID(i), ds_oManagerInfoTmp.GetColumn(0, ds_oManagerInfoTmp.GetColID(i)));

		}

		

		ds_ioManagerInfo.SetColumn(0, "FMHSTARTDATE", left(GetDate(),8));

		ds_ioManagerInfo.SetColumn(0, "FMHENDDATE", "");

		ds_ioManagerInfo.SetColumn(0, "FMHENDSYSDATE", "");

		

		/* 선임자 정보 */

		if(sNewFMGubun == 0) {	//선임자 신규상태

			edManagerNm.Readonly = false;			//성명

			medResidentNo.Readonly = false;			//주민번호

			btnUpdateManagerInfo.Enable = false;	//선임자 정보변경

		} else {

			edManagerNm.Readonly = true;			//성명

			medResidentNo.Readonly = true;			//주민번호

			btnUpdateManagerInfo.Enable = true;		//선임자 정보변경

		}



		/* 초기값 셋팅 */

		if(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {

			lfn_ResidentNoChange(true);		//주민번호 정상

		} else {

			lfn_ResidentNoChange(false);	//주민번호 오류

		}

		lb_IsNiceAuthCheckOk = false;

		

		lfn_searchCust0010ManagerAdditionInfoList();	//선임자 부가정보 조회(겸직정보&보유자격)

		

		// 이미지(사진)

		ls_RealFileName = ds_ioManagerInfo.GetColumn(0, "IIREALNM");

        ls_DirPathName = ds_ioManagerInfo.GetColumn(0, "IIDIRPATH");

        

        if (length(ls_RealFileName) > 0){

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = GF_FILE_SERVER_URL + "kems" + ls_DirPathName + "/" + ls_RealFileName;

            tabTab3.tabpgTab3Page1.imgPicture.Text = "";

        } else {

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = "";

            tabTab3.tabpgTab3Page1.imgPicture.Text = "사진";

        }

        

        medCancelDate.Value = ds_ioManagerInfo.GetColumn(0, "FMHENDSYSDATE");

        

    } else {

		btnUpdateManagerInfo.Enable = false;

		ds_ioManagerInfo.InsertRow(0);	//선임자 입력 가능상태

    }

}



// 업무대행 여부

