// 고객 한방(고객(+고객부가정보)+대상물+선임자 조회)조회

function lfn_SearchAllInfo(sArg) 

{    

    ds_oAllObject.ClearData(); // 데이타셋 초기화



    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010AllSearch", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_oAllObject=ds_oAllObject" // outDs

        , sArg

        , "lfn_AfterSearchAllInfo"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : 고객 한방조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchAllInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

	

	if(ds_oAllObject.rowcount > 0) {

		lfn_SeparateDataset();

		lfn_AfterCustomerProc();

		lfn_AfterBuildingProc();

		lfn_AfterFireManagerProc();

		lfn_AfterCustAdditionProc();
		
		// 연계정보 여부 판단 
		if (edCivilAppeal.Text != "" && edCivilAppeal.Text != null) {
			 if (PDEPTCD == "1031" || PDEPTCD == "1041" || PDEPTCD == "1121" || PDEPTCD == "0243") {
				btnSearchCivilAppeal.Enable = true;
			} else {
				btnSearchCivilAppeal.Enable = false;
			}
			
		} else {
			btnSearchCivilAppeal.Enable = false;
		}

		lfn_SearchAllAddInfo();	//부가정보 한방조회

	} else {

		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.

		lfn_Cancel();				//초기화

		if(length(ls_curComponent) > 0 ) {

			object(ls_curComponent).SetFocus();

		}

    }
}



// 부가정보 한방조회

function lfn_SearchAllAddInfo() {

	

	var lsOutDsListNm = "";

	var lsArgListNm = "";

	

	ds_ioCustomerFeeInfo.ClearData(); 		// 회비/교육비 정보

	ds_ioEducationPassInfo.ClearData(); 	// 실무교육 정보

	ds_ioOwnerInfo.ClearData(); 			// 관계자정보 초기화

	ds_ioDangerInfo.ClearData(); 			// 위험물부가정보 초기화

	ds_ioTankInfo.ClearData(); 				// 이동탱크부가정보 초기화

	ds_ioOverBizInfo.ClearData(); 			// 겸직정보 초기화

	ds_ioLicenseInfo.ClearData(); 			// 보유자격(타자격) 초기화

	ds_oLicenseInfo.ClearData(); 			// 보유자격(안전원자격) 초기화



    tit_clearActionInfo();

    

    if(!gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CMGNO"))) {

		tit_addSearchActionInfo("cust:searchCust2010Fee", false);

		tit_addSearchActionInfo("cust:searchCust0010EducationPassInfoList", false);

		lsOutDsListNm += "ds_ioCustomerFeeInfo=ds_oFee ds_ioEducationPassInfo=ds_ioEducationPassInfo";

		lsArgListNm += "CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(0, "CMGNO")); // 고객정보관리번호

		lsArgListNm += " FEEGUBUN=" + "YEAR";	// 회비/교육비 년도

    }

    if(!gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO"))) {

		tit_addSearchActionInfo("cust:searchCust0010OwnerInfoList", false);

		tit_addSearchActionInfo("cust:searchCust0010DangerInfoList", false);

		tit_addSearchActionInfo("cust:searchCust0010TankInfoList", false);

		lsOutDsListNm += " ds_ioOwnerInfo=ds_ioOwnerInfo ds_ioDangerInfo=ds_ioDangerInfo ds_ioTankInfo=ds_ioTankInfo";

		lsArgListNm += " OBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO")); // 대상물관리번호

		lsArgListNm += " DBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO")); // 대상물관리번호

		lsArgListNm += " TBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO")); // 대상물관리번호

    }

    if(!gfn_IsNull(ds_ioManagerInfo.GetColumn(0, "FMMGNO"))) {

		tit_addSearchActionInfo("cust:searchCust0010OverBizInfoList", false);

		tit_addSearchActionInfo("cust:searchCust0010LicenseInfoList", false);

		tit_addSearchActionInfo("cust:searchCust0010LicenseInfo", false);

		lsOutDsListNm += " ds_ioOverBizInfo=ds_ioOverBizInfo ds_ioLicenseInfo=ds_ioLicenseInfo ds_oLicenseInfo=ds_oLicenseInfo";

		lsArgListNm += " OLFMMGNO=" + quote(ds_ioManagerInfo.GetColumn(0, "FMMGNO")); // 선임자관리번호

		lsArgListNm += " FMRESIDENTNO=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO")); // 선임자주민번호

    }

    

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , lsOutDsListNm // outDs

        , lsArgListNm	// args

        , "lfn_AfterSearchAllAddInfo"

        ,false

    );

}





/*

 * 기능 : 부가정보 한방조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchAllAddInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    // 회비/교육비 정보

    medEmailCnt.Value = 0;	//미납금액

    for(var i=0; i<ds_ioCustomerFeeInfo.GetRowCount(); i++) {

		medEmailCnt.Value = toNumber(medEmailCnt.Value) + toNumber(ds_ioCustomerFeeInfo.GetColumn(i, "MINAPFEE"));

    }

}



// 고객(+고객부가정보)+대상물+선임자 조회값 데이타셋 분리(TLogin 고려)

function lfn_SeparateDataset() {



    ds_ioCustomerInfo.ClearData();

    ds_ioBuildingInfo.ClearData();

    ds_ioManagerInfo.ClearData();

    ds_ioAdditionInfo.ClearData();

    

    ds_ioCustomerInfo.InsertRow(0);

    ds_ioBuildingInfo.InsertRow(0);

    ds_ioManagerInfo.InsertRow(0);

    ds_ioAdditionInfo.InsertRow(0);

    

    if(ds_oAllObject.rowcount == 0) { //데이타 미존재    

        return;

    }

    

    for(var i=0; i<ds_oAllObject.colcount; i++) {

        for(var j=0; j<ds_ioCustomerInfo.colcount; j++) {

            if (ds_oAllObject.GetColID(i) == ds_ioCustomerInfo.GetColID(j)) {

                ds_ioCustomerInfo.SetColumn(0, ds_ioCustomerInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));

            }

        }

        for(var j=0; j<ds_ioBuildingInfo.colcount; j++) {

            if (ds_oAllObject.GetColID(i) == ds_ioBuildingInfo.GetColID(j)) {

                ds_ioBuildingInfo.SetColumn(0, ds_ioBuildingInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));

            }

        }

        for(var j=0; j<ds_ioManagerInfo.colcount; j++) {

            if (ds_oAllObject.GetColID(i) == ds_ioManagerInfo.GetColID(j)) {

                ds_ioManagerInfo.SetColumn(0, ds_ioManagerInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));

            }

        }

        for(var j=0; j<ds_ioAdditionInfo.colcount; j++) {

            if (ds_oAllObject.GetColID(i) == ds_ioAdditionInfo.GetColID(j)) {

                ds_ioAdditionInfo.SetColumn(0, ds_ioAdditionInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));

            }

        }        

    }

    

    ds_ioCustomerInfo.ApplyChange();

    ds_ioBuildingInfo.ApplyChange();	//고려

    ds_ioManagerInfo.ApplyChange();

    ds_ioAdditionInfo.ApplyChange();

}



// 고객 조회 이후(TLogin 고려)

function lfn_AfterCustomerProc() {



    if(!gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CMGNO"))) {

    

		lfn_InitButton(true);						//초기화시 버튼(활성화)

		

		if( ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN") <> "0" ) {	//삭제구분이 있는경우 Disable

			lfn_AfterSearchComponentChange();			//조회 후 상단 조회 내역 변경

			lfn_SetDeleteStatus(false);

		} else {

			lfn_SetDeleteStatus(true);

			lfn_AfterSearchComponentChange();			//조회 후 상단 조회 내역 변경

		}

					

		if(left(cbxCourse.Value,1) == 6) {		//개인회원

			lfn_BuildingInfoStatus(false);	//개인회원인 경우 대상물 정보 수정 불가

		}

				

    } else {

		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.

		lfn_Cancel();				//초기화

		if(length(ls_curComponent) > 0 ) {

			object(ls_curComponent).SetFocus();

		}

    }

}



// 대상물 조회 이후(TLogin 고려)

function lfn_AfterBuildingProc() {



    if( !gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {

		lfn_InitComboBoxIndex(1);				//대상물 콤보 index=0으로 초기화

		cbxStation_OnChanged(Object("cbxStation"), cbxStation.Value);		//대상물 소방관서 강제 이벤트

		ds_ioBuildingInfo.ApplyChange();

	}

}





// 선임자 조회 이후(TLogin 고려)

function lfn_AfterFireManagerProc() {



    if( !gfn_IsNull(ds_ioManagerInfo.GetColumn(0, "FMMGNO")) ) {

		/* 선임자 정보 */

		lfn_FireManagerNewOldGubun(false);	//선임자 기존상태

		edManagerNm.Readonly = true;	//성명

		medResidentNo.Readonly = true;	//주민번호

		if( ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN") <> "0" ) {	//삭제구분이 있는경우 Disable

			btn_SearchFireManager.Enable = false;	//선임자 현황 검색

			btnUpdateManagerInfo.Enable = false;	//선임자 정보변경

		} else {

			btnUpdateManagerInfo.Enable = true;	//선임자 정보변경

		}

		

		/* 초기값 셋팅 */

		if(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTERR") == 0) {

			lfn_ResidentNoChange(true);	//주민번호 정상

		} else {

			lfn_ResidentNoChange(false);	//주민번호 오류

		}

		

		lb_IsNiceAuthCheckOk = false;

		

		if(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHENDGUBUN") == 0) {	//해임

			lfn_EndGubunChange(false);

		} else {

			lfn_EndGubunChange(true);

		}

		

		// 이미지(사진)

		ls_RealFileName = ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "IIREALNM");

        ls_DirPathName = ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "IIDIRPATH");

        

        if (length(ls_RealFileName) > 0){

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = GF_FILE_SERVER_URL + "kems" + ls_DirPathName + "/" + ls_RealFileName;

            tabTab3.tabpgTab3Page1.imgPicture.Text = "";

        } else {

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = "";

            tabTab3.tabpgTab3Page1.imgPicture.Text = "사진";

        }

        

        medCancelDate.Value = ds_ioManagerInfo.GetColumn(0, "FMHENDSYSDATE");

		

	} else {

		btnUpdateManagerInfo.Enable = false;

		ds_ioManagerInfo.ClearData();

		ds_ioManagerInfo.InsertRow(0);	//선임자 입력 가능상태

		lbResidentNoError.Visible = false;	//주민번호 정상/오류

		lbEndStatusYn.Visible = false;		//해임상태

		lfn_FireManagerNewOldGubun(true);	//선임자 신규상태

		edManagerNm.Readonly = false;	//성명

		medResidentNo.Readonly = false;	//주민번호

	}

}



// 고객 부가정보 조회 이후(TLogin 고려)

function lfn_AfterCustAdditionProc() {



    if(ds_ioAdditionInfo.GetRowCount() > 0) {

		

    } else {

		ds_ioAdditionInfo.AddRow();	//입력가능하도록 풀어줌

    }

    lfn_InitComboBoxIndex(3);	//부가정보 Null일때 index=0으로 초기화

}



// 

function lfn_FindNotOverBizFireManagerList(obj) {

	

	//대상물 주소 변경시 주소1,성명,주민번호 가 동일한 경우 겸직이 안된 경우 겸직처리하기

	ds_ioNotOverBizFireManager.ClearData();

	

	//대상물 주소 변경시 대상물관리번호가 다르나 주소1, 이름, 주민번호가 동일해서 겸직이 된 경우 겸직해임처리

	ds_ioIsOverBizFireManager.ClearData();

	

	tit_clearActionInfo();

	tit_addSearchActionInfo("cust:searchCust0010NotOverBizFireManager", false);

	tit_addSearchActionInfo("cust:searchCust0010IsOverBizFireManager", false);

	// 서버 호출 

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_ioNotOverBizFireManager=ds_ioNotOverBizFireManager ds_ioIsOverBizFireManager=ds_ioIsOverBizFireManager"	// outDs

		,"NEWBADDR1=" + quote(ds_ioBuildingInfo.GetColumn(0, "BADDR1"))	// 변경된 대상물 주소1

		+ " OLDBADDR1=" + quote(ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1"))	// 변경전 대상물 주소1

		+ " OLDBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO"))	// 변경전 대상물 관리번호

		,""

		,true

		,true

		,true

	);

}





// 데이타셋 상태 확인 (테스트용)

function Button0_OnClick(obj)

{

	ds_oTest.ClearData();

	ds_oTest.InsertRow(0);

	ds_oTest.SetColumn(0, "BD", ds_ioBuildingInfo.GetRowType(0));

	ds_oTest.SetColumn(0, "CM", ds_ioCustomerInfo.GetRowType(0));

	ds_oTest.SetColumn(0, "FM", ds_ioManagerInfo.GetRowType(0));

	ds_oTest.SetColumn(0, "BBO", ds_ioOwnerInfo.GetUpdate());

	ds_oTest.SetColumn(0, "BBD", ds_ioDangerInfo.GetUpdate());

	ds_oTest.SetColumn(0, "BBT", ds_ioTankInfo.GetUpdate());

	ds_oTest.SetColumn(0, "CMA", ds_ioAdditionInfo.GetRowType(0));

	ds_oTest.SetColumn(0, "FML", ds_ioLicenseInfo.GetUpdate());

	

	for(var i=0; i<ds_oTest.colcount; i++) {

		trace("ColID = " + ds_oTest.GetColID(i) + " :: " + ds_oTest.GetColumn(0, ds_oTest.GetColID(i)));

	}

}



/*

 * 조회 : MAIN (관리번호에 의한 조회)

 *        고객 정보(MAIN 1) - 관리번호

 */

function lfn_searchCust0010CustomerInfo5(sJibu, sCNo, sCNoBeforeYn, sCNoNextYn, sRegCD, SCourseCD) {

	

	if(gfn_IsNull(sJibu) || gfn_IsNull(sCNo)) {	//그룹조회

		gfn_Alert("잘못된 조회입니다.");

		return;

	}

	var sArg = "CNO="	+ quote(sCNo);		// 관리번호

    sArg += " CGTMGNO="	+ quote(sJibu);		        // 지부

    sArg += " CNOBEFOREYN="	+ quote(sCNoBeforeYn);    // 이전번호유무
    
    sArg += " CNONEXTYN="	+ quote(sCNoNextYn);    // 다음번호유무
    
    sArg += " CREGCD="	+ quote(sRegCD);    // 지역
    
    sArg += " CCOURSECD="	+ quote(SCourseCD);    // 직능
    

	lfn_ClearData();	//데이타셋 초기화(모두)

    

    lfn_SearchAllInfo(sArg);

}


/*

 * 조회 : MAIN (>> 버튼 클릭 - 관리번호(CNO)에 의한 조회)

 *        고객 정보(MAIN 1) - 관리번호(CNO)

 */

function btn_NextCNo_OnClick(obj)

{

	if(length(medCustNo.Value) < 1) {

		medCustNo.Value = '000000';

	}

	medCustNo.Value = lpad(medCustNo.Value, "0", 6);

	lfn_searchCust0010CustomerInfo5(trim(cbxJibu.Value), trim(medCustNo.Value), "N", "Y", cbxRegion.Value, cbxDetailCourse.Value);

}

function btn_BeforeCNo_OnClick(obj)
{
	if(length(medCustNo.Value) < 1) {

		medCustNo.Value = '999999';

	}

	medCustNo.Value = lpad(medCustNo.Value, "0", 6);

	lfn_searchCust0010CustomerInfo5(trim(cbxJibu.Value), trim(medCustNo.Value), "Y", "N", cbxRegion.Value, cbxDetailCourse.Value);
	
}

function btnFireSearch_OnClick(obj)
{
	if (ADMINGUBUN == "SM" || ADMINGUBUN = "DM") {
		var parValue = "sCourse="+cbxCourse.Value;
		parValue += " sBarCode="+medBarCode.Value;
		parValue += " sJibu="+cbxJibu.value;
		parValue += " sGubun=customer";
		
		var arrNo = Dialog("cust::frmcust5020.xml", parValue, 800, 700, "", -1, -1);
	} else {
		alert("소방방재청정보는 보안등으로 인하여 회원담당자만 볼 수 있습니다.");
		return;
	}
	
}
