/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oUpdate.ClearData(); // 데이타셋 초기화
    ds_oManual.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("env:searchUpdate", false);
	tit_addSearchActionInfo("env:searchManual", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oUpdate=ds_oUpdate ds_oManual=ds_oManual"	// outDs
		,"BBOARDGUBUN1=" + quote(ls_BoardUpdate)
            + " BBOARDGUBUN2=" + quote(ls_BoardManual)
            + " ROWNUM=" + ls_BoardRownum // args 
		,""
		,false
	);
	
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 최근 UPDATE 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchLatestUpdateManualList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function gdUpdate_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    var sArg = "sBoardSeq=" + quote(ds_oUpdate.GetColumn(nRow, "BSEQ"));
    return Dialog("env::frmenv0024PBoardContents.xml", sArg, 600, 400, "", -1, -1);
}

function gdManual_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    var sArg = "sBoardSeq=" + quote(ds_oManual.GetColumn(nRow, "BSEQ"));
    return Dialog("env::frmenv0024PBoardContents.xml", sArg, 600, 400, "", -1, -1);
}

function imgUpdateMore_OnClick(obj,nX,nY) {
    gs_SystemGubun = "env";
    gs_SystemMenuId = "0000000007";
    global.mnuLeft.tabMenu.tabpgMenu.Text = "환경설정";
    var ls_MenuId = "0000000015";
    var ls_MenuTitle = "UPDATE";
    var ls_MenuUrl = "env::frmenv0030MUpdateList.xml";
    var ls_args = "";

    global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
	gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId, ls_args);
}

