Window.Script:
/*
 * 프로그램명 : frmenv0022PLatestBoard.xml
 * 설명 : 공지사항팝업
 * 작성일 : 2009.07.17
 * 작성자 : 김동현
 */

#include "LIB::env.js"; // 공통 관련

var ls_BoardGubun = "NN";
var ls_BoardRownum = "14";

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    lfn_search(obj); // 최근 UPDATE 조회
}

function Form_OnUnloadCompleted(obj) {
    SetReg("KEMSBoardYn", chkDispYn.Value);
    SetReg("KEMSBoardDate", left(GetDate(), 8));

    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oBoard.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("env:searchBoard", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oBoard=ds_oBoard"	// outDs
		,"BBOARDGUBUN=" + quote(ls_BoardGubun)
            + " ROWNUM=" + ls_BoardRownum // args 
		,"lfn_AfterSearchLatestBoardList"
		,false
	);
}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 최근 UPDATE 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchLatestBoardList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function gdBoard_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    var sArg = "sBoardSeq=" + quote(ds_oBoard.GetColumn(nRow, "BSEQ"))
        + " sFormTitle='공지사항'";
    return Dialog("env::frmenv0024PBoardContents.xml", sArg, 600, 400, "", -1, -1);
}

function imgBoardMore_OnClick(obj,nX,nY) {

    gs_SystemGubun = "env";
    gs_SystemMenuId = "0000000007";
    global.mnuLeft.tabMenu.tabpgMenu.Text = "환경설정";
    var ls_MenuId = "0000000014";
    var ls_MenuTitle = "공지사항";
    var ls_MenuUrl = "env::frmenv0020MBoardList.xml";
    var ls_args = "sBoardGubun=N";

    global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
	gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId, ls_args);

	close();
}
