#include "LIB::item.js"; // 공통업무 관련
 
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
    
	lfn_Search(); // 조회
}

/*
 * 기능 :폼사이즈 조정
 */
function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
		// gdEduItemR.Height = nCy - gdEduItemR.Top - 4;
		// gdEduItemR.Width = nCx - gdEduItemR.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
	if (tabTab.TabIndex == 0) {
		lfn_SearchEduItemRList(); // 교보재분류목록 조회
	} else if (tabTab.TabIndex == 1) {
		tabTab.tabpgTabPage2.edEiBCd = "";
		tabTab.tabpgTabPage2.edEiBNm = "";
		lfn_SearchEduItemBList(); // 대분류코드목록 조회
	} else if (tabTab.TabIndex == 2) {
		tabTab.tabpgTabPage3.edEiMCd = "";
		tabTab.tabpgTabPage3.edEiMNm = "";
		lfn_SearchEduItemMList(); // 중분류코드목록 조회
	} else if (tabTab.TabIndex == 3) {
		tabTab.tabpgTabPage4.edEiSCd = "";
		tabTab.tabpgTabPage4.edEiSNm = "";
		lfn_SearchEduItemSList(); // 소분류코드목록 조회
	}
	
	if (tabTab.TabIndex == 0) {
		if (ds_ioEduItemR.row == -1) {
			mb_IsNewData = true; // 저장버튼이 클릭 설정
			tabTab.tabpgTabPage1.lbStatusR.Text = "입력모드";
		}
	} else if (tabTab.TabIndex == 1) {
		if (ds_ioEduItemB.row == -1) {
			mb_IsNewData = true; // 저장버튼이 클릭 설정
			tabTab.tabpgTabPage2.lbStatusB.Text = "입력모드";
		}
	} else if (tabTab.TabIndex == 2) {
		if (ds_ioEduItemM.row == -1) {
			mb_IsNewData = true; // 저장버튼이 클릭 설정
			tabTab.tabpgTabPage3.lbStatusM.Text = "입력모드";
		}
	} else if (tabTab.TabIndex == 3) {
		if (ds_ioEduItemS.row == -1) {
			mb_IsNewData = true; // 저장버튼이 클릭 설정
			tabTab.tabpgTabPage4.lbStatusS.Text = "입력모드";
		}
	}
}

/*
 * 기능 : 그리드 정렬 및 체크박스 전체선택
 */
function gdEduItemR_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if (nCell > 0) {
		var lobj_Dataset = object(obj.BindDataset);
		// 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
		if (lobj_Dataset.GetRowCount() > 1) {
			gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
		}
	}
}

/*
 * 기능 : 그리드 정렬 및 체크박스 전체선택
 */
