/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnSave.Enable = bStatus; // 저장 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    tabTab.tabpgTabPage1.gdEduItemR.Editable = bStatus;
    tabTab.tabpgTabPage2.gdEduItemB.Editable = bStatus;
    tabTab.tabpgTabPage3.gdEduItemM.Editable = bStatus;
    tabTab.tabpgTabPage4.gdEduItemS.Editable = bStatus;
}

/*
 * 기능 : 폼 키처리
 */
function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

/*
 * 기능 : 초기화
 */
function lfn_Cancel(obj){
	if (tabTab.TabIndex == 0) {
		ds_ioEduItemRUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemRUnit.AddRow();
		tabTab.tabpgTabPage1.cbxEiKiindUseLevel.Index = 0;
	} else if (tabTab.TabIndex == 1) {
		ds_ioEduItemBUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemBUnit.AddRow();
		tabTab.tabpgTabPage2.cbxEiKindGubun.Index = 0;
	} else if (tabTab.TabIndex == 2) {
		ds_ioEduItemMUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemMUnit.AddRow();
		tabTab.tabpgTabPage3.cbxEiKindGubun.Index = 0;
		tabTab.tabpgTabPage3.cbxEiBCd.Index = 0;
	} else if (tabTab.TabIndex == 3) {
		ds_ioEduItemMUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemMUnit.AddRow();
		tabTab.tabpgTabPage4.cbxEiKindGubun.Index = 0;
		tabTab.tabpgTabPage4.cbxEiBCd.Index = 0;
		tabTab.tabpgTabPage4.cbxEiMCd.Index = 0;
	}
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (tabTab.TabIndex == 0) {
		if (!gfn_Confirm("교보재분류를 등록 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.")) {
			return false;
		}
		
		mb_IsNewData = true; // 저장버튼이 클릭 설정
		tabTab.tabpgTabPage1.lbStatusR.Text = "입력모드";
		// tabTab.tabpgTabPage1.edEiKindGubun.Enable = true;
		
		ds_ioEduItemRUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemRUnit.AddRow();
		tabTab.tabpgTabPage1.cbxEiKiindUseLevel.Index = 0;
		tabTab.tabpgTabPage1.edEiKindGubun.SetFocus();	
	} else if (tabTab.TabIndex == 1) {
		if (!gfn_Confirm("대분류코드를 등록 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.")) {
			return false;
		}
		
		mb_IsNewData = true; // 저장버튼이 클릭 설정
		tabTab.tabpgTabPage2.lbStatusB.Text = "입력모드";
		// tabTab.tabpgTabPage2.cbxEiKindGubun.Enable = true;
		// tabTab.tabpgTabPage2.edEiBCd.Enable = true;
		
		var idx = tabTab.tabpgTabPage2.cbxEiKindGubun.Index;
		ds_ioEduItemBUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemBUnit.AddRow();
		tabTab.tabpgTabPage2.cbxEiKindGubun.Index = idx;
		tabTab.tabpgTabPage2.edEiBCd.SetFocus();
	} else if (tabTab.TabIndex == 2) {
		if (!gfn_Confirm("중분류코드를 등록 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.")) {
			return false;
		}
		
		mb_IsNewData = true; // 저장버튼이 클릭 설정
		tabTab.tabpgTabPage3.lbStatusM.Text = "입력모드";
		// tabTab.tabpgTabPage3.cbxEiKindGubun.Enable = true;
		// tabTab.tabpgTabPage3.cbxEiBCd.Enable = true;
		// tabTab.tabpgTabPage3.edEiMCd.Enable = true;
		
		var idx = tabTab.tabpgTabPage3.cbxEiKindGubun.Index;
		var idx2 = tabTab.tabpgTabPage3.cbxEiBCd.Index;
		ds_ioEduItemMUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemMUnit.AddRow();
		tabTab.tabpgTabPage3.cbxEiKindGubun.Index = idx;
		tabTab.tabpgTabPage3.cbxEiBCd.Index = idx2;
		tabTab.tabpgTabPage3.edEiMCd.SetFocus();
	} else if (tabTab.TabIndex == 3) {
		if (!gfn_Confirm("소분류코드를 등록 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.")) {
			return false;
		}
		
		mb_IsNewData = true; // 저장버튼이 클릭 설정
		tabTab.tabpgTabPage4.lbStatusS.Text = "입력모드";
		// tabTab.tabpgTabPage4.cbxEiKindGubun.Enable = true;
		// tabTab.tabpgTabPage4.cbxEiBCd.Enable = true;
		// tabTab.tabpgTabPage4.cbxEiMCd.Enable = true;
		// tabTab.tabpgTabPage4.edEiSCd.Enable = true;
		
		var idx = tabTab.tabpgTabPage4.cbxEiKindGubun.Index;
		var idx2 = tabTab.tabpgTabPage4.cbxEiBCd.Index;
		var idx3 = tabTab.tabpgTabPage4.cbxEiMCd.Index;
		ds_ioEduItemSUnit.ClearData(); // 데이타셋 초기화
		ds_ioEduItemSUnit.AddRow();
		tabTab.tabpgTabPage4.cbxEiKindGubun.Index = idx;
		tabTab.tabpgTabPage4.cbxEiBCd.Index = idx2;
		tabTab.tabpgTabPage4.cbxEiMCd.Index = idx3;
		tabTab.tabpgTabPage4.edEiSCd.SetFocus();
	}
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	// if (!gfn_FormValidate(this)) { // 입력 Validation Check
        // return false;
    // }
    
    if (!gfn_Confirm(MSGW00040)) { // 수정 저장 하시겠습니까?
		return false;
	}
	
    if (tabTab.TabIndex == 0) {
		if (mb_IsNewData) { // 신규 저장
			if (gfn_IsNull(tabTab.tabpgTabPage1.edEiKindGubun.Text)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage1.edEiKindGubun.SetFocus();
				return false;
			}
			
			lfn_SearchEduItemRCheck(); // 교보재분류 중복여부 체크
			
			if (ds_ioEduItemRCheck.GetColumn(0, "CNT") > 0) {
				gfn_Alert("중복된 교보재분류코드가 존재합니다.");
				tabTab.tabpgTabPage1.edEiKindGubun.SetFocus();
				return false;
			}
			
			lfn_InsEduItemRInfo(); // 교보재분류 입력
		} else { // 수정 저장
			if (ds_ioEduItemR.row == -1) {
				gfn_Alert("교보재분류 정보를 등록 하시겠습니까?\n추가버튼을 누르고 입력완료 후 저장 버튼을 누르세요.");
				return false;
			}
			
			lfn_UpdEduItemRInfo(); // 교보재분류 수정
		}
	} else if (tabTab.TabIndex == 1) {
		if (mb_IsNewData) { // 신규 저장
			if (gfn_IsNull(tabTab.tabpgTabPage2.cbxEiKindGubun.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage2.cbxEiKindGubun.SetFocus();
				return false;
			}
			
			if (gfn_IsNull(tabTab.tabpgTabPage2.edEiBCd.Text)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage2.edEiBCd.SetFocus();
				return false;
			}
			
			lfn_SearchEduItemBCheck(); // 대분류코드 중복여부 체크
			
			if (ds_ioEduItemBCheck.GetColumn(0, "CNT") > 0) {
				gfn_Alert("중복된 대분류코드가 존재합니다.");
				tabTab.tabpgTabPage2.edEiBCd.SetFocus();
				return false;
			}
			
			ds_ioEduItemBUnit.SetColumn(0, "EIKINDGUBUNNM", tabTab.tabpgTabPage2.cbxEiKindGubun.Text);
			lfn_InsEduItemBInfo(); // 대분류코드 입력
		} else { // 수정 저장
			if (ds_ioEduItemB.row == -1) {
				gfn_Alert("대분류코드 정보를 등록 하시겠습니까?\n추가버튼을 누르고 입력완료 후 저장 버튼을 누르세요.");
				return false;
			}
			
			lfn_UpdEduItemBInfo(); // 대분류코드 수정
		}
	} else if (tabTab.TabIndex == 2) {
		if (mb_IsNewData) { // 신규 저장
			if (gfn_IsNull(tabTab.tabpgTabPage3.cbxEiKindGubun.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage3.cbxEiKindGubun.SetFocus();
				return false;
			}
			
			if (gfn_IsNull(tabTab.tabpgTabPage3.cbxEiBCd.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage3.cbxEiBCd.SetFocus();
				return false;
			}
			
			if (gfn_IsNull(tabTab.tabpgTabPage3.edEiMCd.Text)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage3.edEiMCd.SetFocus();
				return false;
			}
			
			lfn_SearchEduItemMCheck(); // 중분류코드 중복여부 체크
			
			if (ds_ioEduItemMCheck.GetColumn(0, "CNT") > 0) {
				gfn_Alert("중복된 중분류코드가 존재합니다.");
				tabTab.tabpgTabPage3.edEiMCd.SetFocus();
				return false;
			}
			
			ds_ioEduItemMUnit.SetColumn(0, "EIKINDGUBUNNM", tabTab.tabpgTabPage3.cbxEiKindGubun.Text);
			ds_ioEduItemMUnit.SetColumn(0, "EIBNM", tabTab.tabpgTabPage3.cbxEiBCd.Text);
			lfn_InsEduItemMInfo(); // 중분류코드 입력
		} else { // 수정 저장
			if (ds_ioEduItemM.row == -1) {
				gfn_Alert("중분류코드 정보를 등록 하시겠습니까?\n추가버튼을 누르고 입력완료 후 저장 버튼을 누르세요.");
				return false;
			}
			
			lfn_UpdEduItemMInfo(); // 중분류코드 수정
		}
	} else if (tabTab.TabIndex == 3) {
		if (mb_IsNewData) { // 신규 저장
			if (gfn_IsNull(tabTab.tabpgTabPage4.cbxEiKindGubun.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage4.cbxEiKindGubun.SetFocus();
				return false;
			}
			
			if (gfn_IsNull(tabTab.tabpgTabPage4.cbxEiBCd.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage4.cbxEiBCd.SetFocus();
				return false;
			}
			
			if (gfn_IsNull(tabTab.tabpgTabPage4.cbxEiMCd.Value)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage4.cbxEiMCd.SetFocus();
				return false;
			}
			if (gfn_IsNull(tabTab.tabpgTabPage4.edEiSCd.Text)) {
				gfn_Alert("필수사항 입력완료 후 저장 버튼을 누르세요.");
				tabTab.tabpgTabPage4.edEiSCd.SetFocus();
				return false;
			}
			
			lfn_SearchEduItemSCheck(); // 소분류코드 중복여부 체크
			
			if (ds_ioEduItemSCheck.GetColumn(0, "CNT") > 0) {
				gfn_Alert("중복된 소분류코드가 존재합니다.");
				tabTab.tabpgTabPage4.edEiSCd.SetFocus();
				return false;
			}
			
			ds_ioEduItemSUnit.SetColumn(0, "EIKINDGUBUNNM", tabTab.tabpgTabPage4.cbxEiKindGubun.Text);
			ds_ioEduItemSUnit.SetColumn(0, "EIBNM", tabTab.tabpgTabPage4.cbxEiBCd.Text);
			ds_ioEduItemSUnit.SetColumn(0, "EIMNM", tabTab.tabpgTabPage4.cbxEiMCd.Text);
			lfn_InsEduItemSInfo(); // 중분류코드 입력
		} else { // 수정 저장
			if (ds_ioEduItemS.row == -1) {
				gfn_Alert("소분류코드 정보를 등록 하시겠습니까?\n추가버튼을 누르고 입력완료 후 저장 버튼을 누르세요.");
				return false;
			}
			
			lfn_UpdEduItemSInfo(); // 중분류코드 수정
		}
	}
}

/*
 * 기능 : 교보재분류 입력
 * parameter : 없음
 * Return : 없음
 */
function lfn_InsEduItemRInfo(obj) {
	tit_clearActionInfo();
	tit_AddSingleActionInfo("item:insertItem0001EduItemRInfo", "", 0, "", "N");
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iItemData=ds_ioEduItemRUnit:A" // inDs		
		,""	// outDs
