/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
	if (tabTab.TabIndex == 0) {
		if (ds_ioEduItemR.row == -1) { // 그리드 미선택시
			gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
			return false;
		}
		
		if (mb_IsNewData) { // 신규 저장
			gfn_alert("추가입력 중인 자료가 있습니다.\n저장완료 후 다음 작업을 진행하세요.");
			return false;
		}
				
		if (!gfn_Confirm("선택한 코드의 하위 코드정보가 모두 삭제됩니다.\n삭제 하시겠습니까?")) { // 삭제 하시겠습니까?
			return false;
		}
		
		lfn_DelEduItemRInfo(); // 교보재분류 삭제
		
	} else if (tabTab.TabIndex == 1) {
		if (ds_ioEduItemB.row == -1) { // 그리드 미선택시
			gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
			return false;
		}
		
		if (mb_IsNewData) { // 신규 저장
			gfn_alert("추가입력 중인 자료가 있습니다.\n저장완료 후 다음 작업을 진행하세요.");
			return false;
		}
				
		if (!gfn_Confirm("선택한 코드의 하위 코드정보가 모두 삭제됩니다.\n삭제 하시겠습니까?")) { // 삭제 하시겠습니까?
			return false;
		}
		
		lfn_DelEduItemBInfo(); // 대분류코드 삭제
		
	} else if (tabTab.TabIndex == 2) {
		if (ds_ioEduItemM.row == -1) { // 그리드 미선택시
			gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
			return false;
		}
		
		if (mb_IsNewData) { // 신규 저장
			gfn_alert("추가입력 중인 자료가 있습니다.\n저장완료 후 다음 작업을 진행하세요.");
			return false;
		}
				
		if (!gfn_Confirm("선택한 코드의 하위 코드정보가 모두 삭제됩니다.\n삭제 하시겠습니까?")) { // 삭제 하시겠습니까?
			return false;
		}
		
		lfn_DelEduItemMInfo(); // 대분류코드 삭제
		
	} else if (tabTab.TabIndex == 3) {
		if (ds_ioEduItemS.row == -1) { // 그리드 미선택시
			gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
			return false;
		}
		
		if (mb_IsNewData) { // 신규 저장
			gfn_alert("추가입력 중인 자료가 있습니다.\n저장완료 후 다음 작업을 진행하세요.");
			return false;
		}
				
		if (!gfn_Confirm(MSGW00030)) { // 삭제 하시겠습니까?
			return false;
		}
		
		lfn_DelEduItemSInfo(); // 소분류코드 삭제
	}
}

