 */
function lfn_SearchEduItemRCheck() {
	ds_ioEduItemRCheck.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("item:searchItem0001EduItemRCheck", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iItemData=ds_ioEduItemRUnit:A"	// inDs
		,"ds_ioEduItemRCheck=ds_oItemData" // outDs
		,"" // args 
		,"lfn_AfterSearchEduItemRCheck"
		,false
		,false
		,true
	);
}

