/*
 * 기능 : 탭이동
 */
function tabTab_OnChanged(obj,nOldIndex,nNewindex) {
	if (tabTab.TabIndex == 0) {
		var nRow = 0;
		
		if (ds_ioEduItemR.row <> -1) {
			nRow = ds_ioEduItemR.row;
		}
		
		lfn_SearchEduItemRList(); // 교보재분류목록 조회
		ds_ioEduItemR.row = nRow;
	} else if (tabTab.TabIndex == 1) {
		var nRow = 0;
		
		if (ds_ioEduItemB.row <> -1) {
			nRow = ds_ioEduItemB.row;
		}
		
		lfn_SearchEduItemKindGubun(); // 교보재분류 콤보목록 조회
		
		if (ds_ioEduItemR.row <> -1) {
			tabTab.tabpgTabPage2.cbxEiKindGubun.Value = ds_ioEduItemR.GetColumn(ds_ioEduItemR.row, "EIKINDGUBUN");
		} else {
			tabTab.tabpgTabPage2.cbxEiKindGubun.Index = 0;
		}
		
		tabTab.tabpgTabPage2.edEiBCd = "";
		tabTab.tabpgTabPage2.edEiBNm = "";
		lfn_SearchEduItemBList(); // 대분류코드목록 조회
		ds_ioEduItemB.row = nRow;
	} else if (tabTab.TabIndex == 2) {
		var nRow = 0;
		
		if (ds_ioEduItemM.row <> -1) {
			nRow = ds_ioEduItemM.row;
		}
		
		lfn_SearchEduItemKindGubun(); // 교보재분류 콤보목록 조회
		
		if (ds_ioEduItemB.row <> -1) {
			tabTab.tabpgTabPage3.cbxEiKindGubun.Value = ds_ioEduItemB.GetColumn(ds_ioEduItemB.row, "EIKINDGUBUN");
		} else {
			tabTab.tabpgTabPage3.cbxEiKindGubun.Index = 0;
		}
		
		lfn_SearchEduItemBCode(tabTab.tabpgTabPage3.cbxEiKindGubun.Value); // 대분류코드 콤보목록 조회
		
		if (ds_ioEduItemB.row <> -1) {
			tabTab.tabpgTabPage3.cbxEiBCd.Value = ds_ioEduItemB.GetColumn(ds_ioEduItemB.row, "EIBCD");
		} else {
			tabTab.tabpgTabPage3.cbxEiBCd.Index = 0;
		}
		
		tabTab.tabpgTabPage3.edEiMCd = "";
		tabTab.tabpgTabPage3.edEiMNm = "";
		lfn_SearchEduItemMList(); // 중분류코드목록 조회
		ds_ioEduItemM.row = nRow;
	} else if (tabTab.TabIndex == 3) {
		var nRow = 0;
		
		if (ds_ioEduItemS.row <> -1) {
			nRow = ds_ioEduItemS.row;
		}
		
		lfn_SearchEduItemKindGubun(); // 교보재분류 콤보목록 조회
		
		if (ds_ioEduItemM.row <> -1) {
			tabTab.tabpgTabPage4.cbxEiKindGubun.Value = ds_ioEduItemM.GetColumn(ds_ioEduItemM.row, "EIKINDGUBUN");
		} else {
			tabTab.tabpgTabPage4.cbxEiKindGubun.Index = 0;
		}
		
		lfn_SearchEduItemBCode(tabTab.tabpgTabPage4.cbxEiKindGubun.Value); // 대분류코드 콤보목록 조회
		
		if (ds_ioEduItemM.row <> -1) {
			tabTab.tabpgTabPage4.cbxEiBCd.Value = ds_ioEduItemM.GetColumn(ds_ioEduItemM.row, "EIBCD");
		} else {
			tabTab.tabpgTabPage4.cbxEiBCd.Index = 0;
		}
		
		lfn_SearchEduItemMCode(tabTab.tabpgTabPage4.cbxEiKindGubun.Value, tabTab.tabpgTabPage4.cbxEiBCd.Value); // 중분류코드 콤보목록 조회
		
		if (ds_ioEduItemM.row <> -1) {
			tabTab.tabpgTabPage4.cbxEiMCd.Value = ds_ioEduItemM.GetColumn(ds_ioEduItemM.row, "EIMCD");
		} else {
			tabTab.tabpgTabPage4.cbxEiMCd.Index = 0;
		}
		
		tabTab.tabpgTabPage4.edEiSCd.Text = "";
		tabTab.tabpgTabPage4.edEiSNm.Text = "";
		lfn_SearchEduItemSList(); // 소분류코드목록 조회
		ds_ioEduItemS.row = nRow;
	}
}

