/*
 * 기능 : 데이타셋 Position이 변경된 후
 */
function ds_ioEduItemR_OnRowPosChanged(obj,nOldRow,nRow) {
	ds_ioEduItemRUnit.CopyRow(0, ds_ioEduItemR, ds_ioEduItemR.row);
	ds_ioEduItemRUnit.ApplyChange();
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
	tabTab.tabpgTabPage1.lbStatusR.Text = "수정모드";
	// tabTab.tabpgTabPage1.edEiKindGubun.Enable = false;
}

/*
 * 기능 : 데이타셋 Position이 변경되기 전
 */
function ds_ioEduItemR_CanRowPosChange(obj,nOldRow,nRow) {
	if (nOldRow >= 0 && nRow >= 0) {
		if (ds_ioEduItemRUnit.GetUpdate()) {
			if (gfn_Confirm("변경된 내용이 있습니다.\n변경완료 후 저장버튼을 눌러 저장하시겠습니까?")) {
				return false;
			}
		}
	}
	return true;
}

/*
 * 기능 : 데이타셋 Position이 변경된 후
 */
function ds_ioEduItemB_OnRowPosChanged(obj,nOldRow,nRow) {
