	if (nOldRow >= 0 && nRow >= 0) {
		if (ds_ioEduItemSUnit.GetUpdate()) {
			if (gfn_Confirm("변경된 내용이 있습니다.\n변경완료 후 저장버튼을 눌러 저장하시겠습니까?")) {
				return false;
			}
		}
	}
	return true;
}

/*
 * 기능 : 대분류코드 교보재분류 콤보값 변경
 */
function tabTab_tabpgTabPage2_cbxEiKindGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if (!mb_IsNewData) {
		lfn_Search(); // 조회
	}
}

