Window.Script:
/*
 * 프로그램명 : frmitem0005PEduScheduleList.xml
 * 설명 : 실무교육일정검색팝업
 * 작성일 : 2009.12.16
 * 작성자 : 김종복
 */

#include "LIB::item.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
	// 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
    gfn_SearchCustomerCourseFullName(ds_oCourse, "0", "전체"); // 대표직능코드 조회
    
    if (IsExistVar("sJibuCode")) {
	    cbxJibu.Value = sJibuCode;
    }
    
    if (IsExistVar("sEduYear")) {
	    if (length(sEduYear) == 0) {
			edEduYear.Text = left(GetDate(), 4);
	    } else {
			edEduYear.text = sEduYear;
	    }
	}
    
    if (IsExistVar("sCourseCode")) {
        cbxCourse.Value = sCourseCode;
    }
    
    lfn_Search(); // 조회
}

/*
 * 기능 : Form이 종료되거나 화면 전환시
 */
function Form_OnUnloadCompleted(obj) {
    Close(); // 종료
}

/*
 * 기능 : Form 또는 Form에 있는 Component에서 Key가 눌러 졌을 때
 */
function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oEduSchedule.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("item:searchItem0005PopUpEduScheduleList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEduSchedule=ds_oItemData" // outDs
		,"JIBUCODE=" + quote(cbxJibu.Value) //-- 지부
			+" EDUYEAR=" + quote(edEduYear.Text) //-- 년도 
			+" COURSECODE=" + quote(cbxCourse.Value) //-- 직능 // args 
			+" EDUORDER=" + quote(edEduOrder.Text) //-- 교육 회차 
		,"lfn_AfterSearchPopUpEduScheduleList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchPopUpEduScheduleList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
	// if (ds_oEduSchedule.GetRowCount() == 1) {
        // btnSelect_OnClick();
    // }
    
    gfn_SearchMessage(gdEduSchedule); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(); // 종료
}

/* 공통 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 그리드 멀티정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEduSchedule, 0); // 멀티 정렬을 실행한다.
}

/*
 * 기능 : 선택
 */
function btnSelect_OnClick(obj) {
    if (ds_oEduSchedule.row == -1) {
        Close();
    }
     
    var arrArg = Array();
    arrArg[0] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESDATE"); // 교육일자
    arrArg[1] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESSTARTTIME"); // 교육시작시간
    arrArg[2] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESENDTIME"); // 교육종료시간
    arrArg[3] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESGUBUN"); // 교육구분
    arrArg[4] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESYEAR"); // 교육년도
    arrArg[5] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESSTUDENT"); // 교육대상
    arrArg[6] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "CGROUPNM"); // 대표직능명
    arrArg[7] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESSUBJECTGUBUN"); // 교육과정
    arrArg[8] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESMGNO"); // 실무교육과정일정관리번호
    arrArg[9] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESCOUSECD"); // 대표직능코드
    arrArg[10] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "EDUTIME"); // 교육시간
    arrArg[11] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESACCESSCNT"); // 접수자수
    arrArg[12] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESSEATCNT"); // 좌석수
    arrArg[13] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "EPNM"); // 교육장소
    arrArg[14] = ds_oEduSchedule.GetColumn(ds_oEduSchedule.row, "ESNOTICE"); // 안내

    
    Close(arrArg);
}

/*
 * 기능 : Head 영역의 Cell을 Click
 */
function gdEduSchedule_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 키다운
 */
function edYear_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_Search(); // 조회
	}
}

/*
 * 기능 :  Body 영역의 Cell을 Double Click 
 */
function gdEduSchedule_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	btnSelect_OnClick(obj);
}
