Window.Script:
/*
 * 프로그램명 : frmitem0006PTrainingOrderList.xml
 * 설명 : 강습교육일정검색팝업
 * 작성일 : 2009.12.16
 * 작성자 : 김종복
 */

#include "LIB::item.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부코드조회
	gfn_SearchTrainingCourseMunje("T", ds_oCourse, "Y", "전체"); // 직능코드조회

    if (IsExistVar("sJibu")) {
        cbxJibu.Value = sJibu;
    }

	if (IsExistVar("sYear")) {
        if (length(sYear) == 0) {
			edTrainingYear.Text = left(GetDate(), 4);
	    } else {
			edTrainingYear.Text = sYear;
	    }
    }

    if (IsExistVar("sCourse")) {
        cbxCourse.Value = sCourse;
    }

    if (sNoChange == "Y") {
        edTrainingYear.Enable = false;
        cbxCourse.Enable = false;
        cbxJibu.Enable = false;
    }

	if (!(gfn_IsNull(cbxCourse.Value) && gfn_IsNull(edTrainingYear.Text))) {
		lfn_Search(); // 조회
    }
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oTrainingOrder.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("item:searchItem0006PopUpTrainingOrderList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingOrder=ds_oItemData"	// outDs
		,"TCCOURSECD=" + quote(cbxCourse.Value)
            + " TOMGJIBUCD=" + quote(cbxJibu.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " POSIYN=" + quote(sPosiYn) // 접수가능여부
		,"lfn_AfterSearchTrainingOrder"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close("");
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingOrder(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    // if (ds_oTrainingOrder.GetRowCount() == 1) {
        // btnSelect_OnClick();
    // }

    gfn_SearchMessage(gdTrainingOrder); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Head 영역의 Cell을 Click
 */
function gdTrainingOrder_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 그리드 멀티정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdTrainingOrder, 0); // 멀티 정렬을 실행한다.
}

/*
 * 기능 : 선택
 */
function btnSelect_OnClick(obj) {
    if (ds_oTrainingOrder.GetCount() < 1) {
        Close();
    }

    var arrArg = Array();
    arrArg[0] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOMGNO"); // 관리번호
    arrArg[1] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOMGGTMGNO"); // 지부코드
    arrArg[2] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOYEAR"); // 년도
    arrArg[3] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "COURSEMUNJECD"); // 직능코드
    arrArg[4] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOTRAININGORDER"); // 회차
    arrArg[5] = ds_oCourse.GetColumn(ds_oCourse.FindRow("CD", ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "COURSEMUNJECD")), "DATA") ; // 직능명
    arrArg[6] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOSTARTDATE"); // 교육일정시작
    arrArg[7] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOENDDATE"); // 교육일정종료
    arrArg[8] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOJUBSUCNT"); // 접수인원수
    arrArg[9] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOSTARTTIME"); // 시작시간
    arrArg[10] = ds_oTrainingOrder.GetColumn(ds_oTrainingOrder.currow, "TOENDTIME"); // 종료시간
    Close(arrArg);
}

/*
 * 기능 :  Body 영역의 Cell을 Double Click 
 */
function gdTrainingOrder_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

/*
 * 기능 : 키다운
 */
function edTrainingYear_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) {
        lfn_Search(); // 조회
    }
}
