 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEduItemRCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

