/*
 * 기능 : 조회
 */
function lfn_Search() {
	lfn_SearchEduItemHList(); // 교보재수불현황 조회
}

/*
 * 기능 : 교보재수불현황 조회
 * parameter : 없음
 * Return : 없음
 */
function lfn_SearchEduItemHList() {
	ds_ioEduItemH.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();

	tit_addSearchActionInfo("item:searchItem0003EduItemHList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEduItemH=ds_oItemData" // outDs
		,"GTMGNO=" + quote(cbxJibuSearch.Value) //-- 부서&지부
			+" RCODE=" + quote(cbxEiRCdSearch.Value) // -- 교보재분류
			+" BCODE=" + quote(cbxEiBCdSearch.Value) // -- 대분류
			+" MCODE=" + quote(cbxEiMCdSearch.Value) // -- 중분류 
			+" SCODE=" + quote(cbxEiSCdSearch.Value) // -- 소분류  
			+" YEAR=" + quote(cbxYearSearch.Value) // -- 대상년도  
			+" SUBULYMD=" + quote(calSubulDateSearch.Value) // -- 수불일자 //arg
			+" SEARCH_GUBUN=" + "1"  // 교재, 제서식(수불현황) 구분
		,"lfn_AfterSearchEduItemHList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 교보재수불현황 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEduItemHList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    medTotalCnt.Value = ds_ioEduItemH.GetRowCount();
    gfn_SearchMessage(gdEduItemH); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

