    if (gs_IsResizingForm == "Y") {
		// gdEduItemH.Height = nCy - gdEduItemH.Top - 4;
		// gdEduItemH.Width = nCx - gdEduItemH.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search() {
	lfn_SearchEduItemList(); // 코드목록 조회
}

/*
 * 기능 : 코드목록 조회
 * parameter : 없음
 * Return : 없음
 */
function lfn_SearchEduItemList() {
	ds_ioEduItem.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("item:searchItem0009EduItemList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEduItem=ds_oItemData" // outDs
		," EIKINDGUBUN=" + quote(cbxEiRCdSearch.Value) // -- 교보재분류
			+" EIBCD=" + quote(cbxEiBCdSearch.Value) // -- 대분류
			+" EIMCD=" + quote(cbxEiMCdSearch.Value) // -- 중분류  // args 
		,"lfn_AfterSearchEduItemList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 코드목록 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEduItemList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    medTotalCnt.Value = ds_ioEduItem.GetRowCount();
    gfn_SearchMessage(gdEduItem); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 그리드 정렬 및 체크박스 전체선택
 */
function gdEduItem_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if (nCell > 0) {
		var lobj_Dataset = object(obj.BindDataset);
		// 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
		if (lobj_Dataset.GetRowCount() > 1) {
			gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
		}
	}
}

/*
 * 기능 : 다중정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEduItem, 1); // 멀티 정렬을 실행한다.
}

