	var querySQL;
  var errSl;

querySQL  = "  SELECT                                ";
querySQL += "       M.MMENUID AS MMENUID             ";
querySQL += "     , M.MMENUNM AS MMENUNM             ";
querySQL += "     , IIF(M.MPARENTMENUID = '0000000071', 2, 1) AS MENULEVEL           ";
