function Run()
{
	var querySQL;
  var errSql;

  querySQL  = "  SELECT                                  ";
	querySQL += "         MMENUID AS MMENUID               ";
	querySQL += "       , MMENUNM AS MMENUNM               ";
	querySQL += "       , '2' AS MENULEVEL                 ";
	querySQL += "       , M.MREMARK AS MREMARK             ";
	querySQL += "       , M.MURL AS MURL                   ";
	querySQL += "       , M.MURLTYPE AS MURLTYPE           ";
	querySQL += "       , M.MUSEYN AS USE_YN               ";
	querySQL += "       , M.MPARENTMENUID AS MPARENTMENUID "; 
	querySQL += "       , M.MORDERSEQ AS MORDERSEQ         ";
	querySQL += "       , M.MSYSGUBUN AS MSYSGUBUN         ";
	querySQL += "    FROM MENU M                           ";
	querySQL += "   WHERE M.MPARENTMENUID = '0000000001'   ";
	querySQL += "   ORDER BY M.MORDERSEQ                   ";

  errSql = ExecuteQuery(querySQL, "output");

}
