										+ "	AND CAITEMNO = " + dsGetColumn(in_Excel,i,"CAITEMNO") + ";";
		var error = ExecuteSql(insertQuery);  
	}
	//trace(insertQuery);

}

function dsGetColumn(ds,row,colid)
{

    return ds.getcolumn(row,colid);

}
