function Run() {

	var errSql;
	var updateQuery;
	
	for ( var i=0; i < input.getRowCount(); i++ ) {
	
		if( dsGetColumn(input,i,"SEL") == "1" ){
			updateQuery  = " UPDATE SERVERSENDH       ";
			updateQuery += " SET SSHSENDYN = 'Y'      ";
			updateQuery += " WHERE SSHEOMGNO = '" + dsGetColumn(input, i, "SSHEOMGNO") + "'";
			updateQuery += " AND SSHREPEATNO = '" + dsGetColumn(input, i, "SSHREPEATNO") + "'";
					
			errSql = ExecuteSql(updateQuery);
			trace(errSql +" :: " + updateQuery);		
		}
	}
}

function dsGetColumn(ds,row,colid) {
    return ds.getcolumn(row,colid);
}
