/*

 * 조회(F2)

 */

function lfn_Search(obj)

{
	// 조회조건이 아무것도 입력되어 있지 않으면 조회 불가
	if (gfn_IsNull(edMemberNm.Text) && gfn_IsNull(medBirthday.Value) &&
	
	    gfn_IsNull(medPersonKey.Value) && gfn_IsNull(medCustNo.Value) &&
	     
	    gfn_IsNull(medBarCode.Value)) {

		gfn_Alert("조회조건을 입력 후 조회해 주세요!");
		
		return false;
		
	}

	lfn_ClearData();
	
	medCustNo.Value = iif(gfn_IsNull(medCustNo.Value), "", lpad(medCustNo.Value, "0", 6));

	var sArg = "CGTMGNO="	+ quote(cbxJibu.Value); 	// 지부코드

    sArg += " CNO=" + quote(medCustNo.Value);		// 고객번호
    
    sArg += " FMNM="	+ quote(edMemberNm.Text); 	// 성명
    
    sArg += " FMBIRTHDAY="	+ quote(medBirthday.Value); 	// 생년월일
    
    sArg += " FMPERSONKEY="	+ quote(medPersonKey.Value); 	// 개인식별번호
    
    sArg += " CMGNO="	+ quote(medBarCode.Value); 	// 관리바코드
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부코드=" + cbxJibu.Value
                + ", 관리번호=" + medCustNo.Value
                + ", 성명=" + edMemberNm.Text
                + ", 생년월일=" + medBirthday.Value
                + ", 개인식별번호=" + medPersonKey.Value
                + ", 관리바코드=" + medBarCode.Value;

                
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );


    ds_oMemberInfo.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();

    tit_addSearchActionInfo("mem:searchMem0010AllSearch", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_oMemberInfo=ds_oMemberInfo" // outDs

        , sArg

        , "lfn_AfterSearchAllInfo"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : 고객 한방조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchAllInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }


	if(ds_oMemberInfo.rowcount > 0) {

		// ds_oMemberInfo_OnRowPosChanged 에서 처리			//lfn_SearchAllAddInfo();	//부가정보 한방조회

	} else {

		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.

		lfn_Cancel();				//초기화

		if(length(ls_curComponent) > 0 ) {

			object(ls_curComponent).SetFocus();

		}

    }

}



// 부가정보 한방조회

function lfn_SearchAllAddInfo() {

	

	var lsOutDsListNm = "";

	var lsArgListNm = "";

	

	ds_ioCustomerFeeInfo.ClearData(); 		// 회비/교육비 정보

	ds_ioEducationPassInfo.ClearData(); 	// 실무교육 정보

	ds_ioOverBizInfo.ClearData(); 			// 겸직정보
	
	//ds_oMemJoinH.ClearData(); 				// 회원가입탈퇴이력

    tit_clearActionInfo();

    

    if(!gfn_IsNull(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO"))) {

		tit_addSearchActionInfo("cust:searchCust2010Fee", false);

		tit_addSearchActionInfo("cust:searchCust0010EducationPassInfoList", false);

		//tit_addSearchActionInfo("mem:searchMem0011MemJoinH", false);	// 회원가입탈퇴이력

		lsOutDsListNm += "ds_ioCustomerFeeInfo=ds_oFee ds_ioEducationPassInfo=ds_ioEducationPassInfo ds_oMemJoinH=ds_oMemJoinH";

		lsArgListNm += "CMGNO=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO")); // 고객정보관리번호

		lsArgListNm += " FEEGUBUN=" + "YEAR";	// 회비/교육비 년도

    }

    if(!gfn_IsNull(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "FMMGNO"))) {

		tit_addSearchActionInfo("cust:searchCust0010OverBizInfoList", false);

		lsOutDsListNm += " ds_ioOverBizInfo=ds_ioOverBizInfo";

		lsArgListNm += " OLFMMGNO=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "FMMGNO")); // 선임자관리번호
		
		lsArgListNm += " FMNM=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MNAME")); // 선임자명

		lsArgListNm += " FMPERSONKEY=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MPERSONKEY")); // 선임자 개인식별번호
		
		lsArgListNm += " CMGNO=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO")); // 고객정보관리번호

    }

    

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , lsOutDsListNm // outDs

        , lsArgListNm	// args

        , "lfn_AfterSearchAllAddInfo"

        ,false

    );

}





/*

 * 기능 : 부가정보 한방조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchAllAddInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

}


/*

 * 기능 : 초기화(F10)

 */

function lfn_Cancel(obj) 

{

    lfn_ClearData();					//초기화 (모든 데이터셋)	

	lfn_ClearSearchCompornent();		//조회정보 초기화


}



/*

 * 기능 : 초기화 (모든 데이터셋)

 */

function lfn_ClearData() {

	//Dataset Clear

	ds_oMemberInfo.ClearData(); 		// 회원 정보(MAIN 1)


	ds_ioCustomerFeeInfo.ClearData();	// 회비/교육비 정보

	ds_ioEducationPassInfo.ClearData();	// 실무교육 정보

	ds_oEduConfirmPrint.ClearData();	// 실무교육이수확인증 정보


	ds_ioOverBizInfo.ClearData();		// 겸직 정보

}




/*

 * 기능 : 조회 초기화

 */

function lfn_ClearSearchCompornent() {

	edMemberNm.Value = "";
	
	medBirthday.Value = "";
	
	medPersonKey.Value = "";

	cbxJibu.Value = "";
	
	medCustNo.Value = "";

	medBarCode.Value = "";
	
}



