// 실무교육 이수확인증 출력
function tabTab2_tabpgTab2Page2_gdEduInfo_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 0) {
	
		if (ds_ioEducationPassInfo.GetColumn(nRow,"EPSUCCESSIONGUBUN") <> "2") {
		
			ds_ioEducationPassInfo.row = nRow;   
		
			var sArg = "sMsg=" + quote("실무교육 이수확인증을 출력하시겠습니까?"); // 대상물관리번호
		
			var msg_result = Dialog(ls_Prefix + "::frmcust0010PEduIssuePrintMessage.xml", sArg, 500, 100, "", -1, -1);
	
		
			var	isunumYear = "";
			var	isunumSeq = "";
		
			if (msg_result == "0") { //미리보기
				lfn_EduIssueConfirmSearch();	// 교육이수내역 조회
				isunumYear = "";
				isunumSeq = "";
	
			} else if (msg_result == "1") {  //출력(고객요청시)
			
				print_result = ext_MsgBox ("실무교육 이수확인증 출력",
										"출력시 발급번호가 생성됩니다.\n\n고객께 발급해 드릴경우만 출력하시고, 확인용은 [미리보기]를 눌러 확인하세요!\n\n출력하시겠습니까?",
										"MB_ICONQUESTION|MB_YESNO|MB_DEFBUTTON2");
			
				if (print_result == "6") { // Yes
					lfn_EduConfirmInsertProc();		// 이수확인서 발급Table insert (FIRENET.EDUCONFIRM)
					lfn_EduIssueConfirmSearch();	// 교육이수내역 조회
					
					isunumYear = left(GetDate(), 4);
					isunumSeq = ds_oEduConfirmPrint.GetColumn(0, "ECMGNO");
				} else if (print_result == "7") { // No
					return;
				}
			} else if (msg_result == "2") {  // 닫기
				return;
			
			}	
		
			var ls_RexFile = "";
			// 출력물보안 미적용 리포트
			ls_RexFile = "frmCust0010R01EduPassPrint.rex";
		
	
			gfn_CallReportPopup(ls_RexFile
				, "ds_oEduConfirmPrint"
				  , "epmgno=" + ds_ioEducationPassInfo.GetColumn(ds_ioEducationPassInfo.currow, "EPMGNO")
				   + "^isunumYear=" + isunumYear
				   + "^isunumSeq=" + isunumSeq
				, true
				, ""	// subreport
				, 250	// 상단여백
				, 150	// 왼쪽여백
			);
		}
	}
}

// 이수확인서 발급Table insert (FIRENET.EDUCONFIRM)
function lfn_EduConfirmInsertProc() {

	tit_clearActionInfo();
	tit_addSingleActionInfo("cust:insertCust0010EduConfirmPrint", "", 0, "");

	tit_callService(
		""
		,""
		,"" // inDs
		,"" // outDs
		,"EPYEAR=" + quote(ds_ioEducationPassInfo.GetColumn(ds_ioEducationPassInfo.currow, "EPYEAR"))
		 + " EPCMGNO=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO"))
		 + " EPMGNO=" + quote(ds_ioEducationPassInfo.GetColumn(ds_ioEducationPassInfo.currow, "EPMGNO"))
		,""
		,false
		,false
		,true
	);
		
}

// 교육이수내역 조회
function lfn_EduIssueConfirmSearch() {

	ds_oEduConfirmPrint.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0010EduConfirmPrint", "", 0, "");
	
	tit_callService(
		""
		,""
		,"" // inDs
		,"ds_oEduConfirmPrint=ds_oEduConfirmPrint" // outDs
		,"ECEDUMGNO=" + quote(ds_ioEducationPassInfo.GetColumn(ds_ioEducationPassInfo.currow, "EPMGNO"))
		,""
		,false
		,false
		,true
	);

}

