// 은행 가상계좌 보기
function btnShowVirtualAccount_OnClick(obj)
{
	// 고객자료가 조회된 상태이면
	if(ds_oMemberInfo.rowcount > 0) {
	
		if (ds_oMemberInfo.GetColumn(0, "CDELGUBUN") == "0") {
		
			if (ds_oMemberInfo.GetColumn(0, "CSTATUSGUBUN") == "0") { // 회원이면 가상계좌 보이기
			
				// 세틀뱅크 가상계좌 보기
				sArg = "sGUBUN=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CSTATUSGUBUN"));
				sArg += " sTotalAmount=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MINAPFEE"));
				sArg += " sJibu=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CGTMGNO"));
				sArg += " sCMgno=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO"));
				sArg += " sFMNM=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MNAME"));
				sArg += " sHPTEL=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MHP"));
				sArg += " sBNM=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "MBNM"));
				sArg += " sCOURSENM=" + quote(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CNICKNM"));
				
				
				vRtl = Dialog("cust::frmcust2018PFixVirtualAccount.xml", sArg, 400, 220, "", -1, -1);	

			} else {	// 비회원이면 
			
				if (gfn_Confirm("비회원은 실무교육 접수시에만 가상계좌를 확인 및 부여할 수 있습니다.\n\n"+
				            "       1. '확인' : [실무교육 접수관리] 바로가기 ☞\n\n"+
				            "       2. '취소' : 닫기") == "1") {
				       
				       btnEduJubsu_OnClick(obj);
				            
				}
			
			}

		} else {

			gfn_Alert("삭제된 고객은 가상계좌가 없습니다.");
			return;
		
		}
	} else {

		gfn_Alert("고객정보를 조회하신 후 가상계좌보기를 클릭해주세요!");
		return;
		
	}
}


