// 교육안내문 출력
function lfn_EduDataPrint(obj)
{
var issuechk = "";	// 교육이수 체크

	if( gfn_IsNull(ds_oMemberInfo.GetColumn(ds_oMemberInfo.currow, "CMGNO")) ) {

		gfn_Alert("교육안내문을 발행할 자료가 없습니다.");

		return;
	
	}

	for(var i=0; i<ds_ioEducationPassInfo.GetRowCount(); i++) {

		// 당해년도 교육이수사항이 있는지 체크
		if ((left(ds_ioEducationPassInfo.GetColumn(i, "EPPASSDATE"), 4) == left(GetDate(), 4)) &&
		   (ds_ioEducationPassInfo.GetColumn(i, "EPSUCCESSIONGUBUN") == "0" ||	// 선임자교육
		    ds_ioEducationPassInfo.GetColumn(i, "EPSUCCESSIONGUBUN") == "1" ||	// 교육승계
		    ds_ioEducationPassInfo.GetColumn(i, "EPSUCCESSIONGUBUN") == "4")) {	// 교육이관
		    issuechk = "Y";
		    break;
		} else {	// 당해년도 교육이수내역이 없거나 2:사이버교육,3:이수후퇴직인 경우
			issuechk = "N";
		}

	}

	if (issuechk == "Y") {	// 당해년도 교육이수사항이 있으면
	
		gfn_Alert(left(GetDate(), 4)+"년도 교육이수 내역이 있습니다. 교육안내문 자료가 생성되지 않습니다.!");
		
		return false;	
	}
	
    if (!gfn_Confirm("교육안내문 출력을 하면 교육안내문 발급이력이 생성됩니다.\n"
                +"진행하시겠습니까?")) { // 
        return false;
    }

	// 대상자 조회
	lfn_EduNoticeSearch();
	
    lfn_EduNoticeHistorySysdate();  // SYSDATE를 구함
    
    lfn_EduNoticeHistory3();        //  교육통지서 이력 넣기  추출대상자 넣기  // 컬럼 추가 필요(sysdate)

    lfn_EduNoticeHistory6();            /// 해당 그룹에 대해 발급 유무 초기화 시키기              // 컬럼 검색조건 필요(sysdate)

    lfn_EduNoticeHistory2();            /// 선택한 통지서 발행 인원 이력에 발급 유 업데이트 하기  // 컬럼 검색조건 필요(sysdate)
  
    
	gfn_CallReportPopup("frmCust1107R01EduDataImage.rex"
			,"ds_ioMEduDataDetail"    // ds
			,""                  // 매개변수
			,""                  // 수첩여부 
			,""                  // 서브레포트 사용여부
			,""
			,""
	);

}

// 대상자 조회
