        + " cbxBusinessType_4=''"								    //용도_다중선택 // 소방안전관리자만 적용 
        + " cbxBusinessType_5=''"								    //용도_다중선택 // 소방안전관리자만 적용 
        + " cbxBusinessType_6=''"								    //용도_다중선택 // 소방안전관리자만 적용 
        + " radReport='0'"											//선임신고일 전체_선택 유무
        + " cbxReportData1=''"										//선임신고일1 (from)
