/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AftersearchEdu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    } 
    
    lfn_Search_EducationList();  // key값으로 대상리스트 상세 조회함 

//    trace(ds_ioMEduData.GetColumn(0, "fkeyA"));   //키값 출력해봄
//    trace(ds_ioMEduData.Count());   //키값 출력해봄        
}


/*
 *  키값으로 교육안내대상리스트 상세 조회 
*/
function lfn_Search_EducationList()
{
    ds_ioMEduDataDetail.ClearData(); // 데이터셋 초기화        	 
    tit_clearActionInfo();
    tit_AddSearchActionInfo("cust:searchCust1107FireDefDMDataDetail", false);        

    tit_callService(
        ""
        ,""
        ,""// inDs
		,"ds_ioMEduDataDetail=ds_oMEduDataDetail"  // outDs
        ,"fkeyA=" + quote(ds_ioMEduData.GetColumn(0, "fkeyA"))
         + " TEST="+"--/*test*/"
          //args                                         
        ,"lfn_AfterSearchEducationList"
        ,false
        ,""
        ,true
        
    );
}

function lfn_AfterSearchEducationList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
   
}

