/*
*  교육안내문 추출 총인원 이력 넣기 
*/
function lfn_EduNoticeHistory3()
{
  
	// 자료 넣기	
	tit_clearActionInfo();
    tit_AddSingleActionInfo(	
        "cust:insertCust1107EduDataHistory"	// insert  		
        , ""	// update  
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iEduDataHistory=ds_ioMEduDataDetail:U"/// inDs		
        ,""	// outDs
        , "GET_SYSDATE=" + quote(ds_ioEduNoticeHistoryGetSysdate.GetColumn(0, "CHAR_SYSDATE")) // "JIBU_A=" + quote(cbxJibu.value)  //args            
        ,"lfn_AftersearchCust"
        ,false
        ,""
        ,true
        
    );  

}

