function Button0_OnClick(obj)
{
	var sFileName;
	if (FileDialog0.Open() == true) 
	{
		var ExportObject1;
		
		ExportObject1 = CreateExportObject();
		ExportObject1.ExportType = "Excel";
		ExportObject1.ExportFileName = FileDialog0.FilePath+"\\"+ FileDialog0.FileName;
		ExportObject1.ActiveSheetName = "sheet1";
		ExportObject1.MakeEmptyFileWhenNotExist = true; //바로열기
		ExportObject1.AddExportGrid("본부" + "!" + "A1", gdList, true, false);
		ExportObject1.AddExportGrid("지부" + "!" + "A1", gdList3, true, false);
		
		ExportObject1.Export();
		ExportObject1.Close();
		ExportObject1 = null;
	}	
}
