Window.Script:
/*
 * 프로그램명 : frmPFM0001S01ProfMgmt.xml
 * 설명 : 사용자정보검색팝업
 * 작성일 : 2009.11.06
 * 작성자 : 김종복
 */

#include "LIB::profes.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sInsaCode")) {
        edInsaCode.Text = sInsaCode;
    }
    
    if (IsExistVar("sUserName")) {
        edUserNm.Text = sUserName;
    }

    lfn_Search(); // 조회
}

/*
 * 기능 : Form이 종료되거나 화면 전환시
 */
function Form_OnUnloadCompleted(obj) {
    Close(); // 종료
}

/*
 * 기능 : Form 또는 Form에 있는 Component에서 Key가 눌러 졌을 때
 */
function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oProfUser.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("profes:searchProfes0001PopUpProfUserList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oProfUser=ds_oProfData" // outDs
		,"INSACODE=" + quote(edInsaCode.Text) //-- 사번
			+" USERNAME=" + quote(edUserNm.Text) //-- 성명 // args 
		,"lfn_AfterSearchPopUpProfUserList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 사용자정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchPopUpProfUserList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
	// if (ds_oProfUser.GetRowCount() == 1) {
        // btnSelect_OnClick();
    // }
    
    gfn_SearchMessage(gdProfUser); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(); // 종료
}

/*
 * 기능 : Head 영역의 Cell을 Click
 */
function gdProfUser_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 그리드 멀티정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdProfUser, 0); // 멀티 정렬을 실행한다.
}

/*
 * 기능 : 선택
 */
function btnSelect_OnClick(obj) {
    if (ds_oProfUser.row == -1) {
        Close();
    }
    
    var arrArg = Array();
    arrArg[0] = ds_oProfUser.GetColumn(ds_oProfUser.row, "INSACODE");
    arrArg[1] = ds_oProfUser.GetColumn(ds_oProfUser.row, "USERNAME");
    arrArg[2] = ds_oProfUser.GetColumn(ds_oProfUser.row, "JIKRYUL");
    arrArg[3] = ds_oProfUser.GetColumn(ds_oProfUser.row, "DEPARTNM");
    Close(arrArg);
}

/*
 * 기능 :  Body 영역의 Cell을 Double Click 
 */
function gdProfUser_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

/*
 * 기능 : 키다운
 */
function edUserNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_Search(); // 조회
	}
}

/*
 * 기능 : 키다운
 */
function edInsaCode_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_Search(); // 조회
	}
}
