Window.Script:
/*
 * 프로그램명 : frmPFM0001S04ProfMgmt.xml
 * 설명 : 논문 및 연구실적
 * 작성일 : 2009.11.09
 * 작성자 : 김종복
 */

#include "LIB::profes.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    tit_createActionInfo(); // X-Framework용    
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }    
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    gdProfPaper.Editable = bStatus;
}

/*
 * 기능 : 그리드정렬
 */
function gdProfPaper_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1 && nCell > 0) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 행추가
 */
function btnAdd_OnClick(obj) {
	ds_ioProfPaper.AddRow(); // Dataset에 빈 Record한줄 추가
	ds_ioProfPaper.SetColumn(ds_ioProfPaper.row, "INSACODE", ds_ioProfUser.GetColumn(ds_ioProfUser.row, "INSACODE"));
}

/*
 * 기능 : 행삭제
 */
function btnDel_OnClick(obj) {
	ds_ioProfPaper.DeleteRow(ds_ioProfPaper.row);
}

/*
 * 기능 : 논문 및 연구실적 조회
 * parameter : 없음
 * Return : 없음
 */
function lfn_SearchProfPaper(obj) {
	gdProfPaper.Redraw = false;

	ds_ioProfPaper.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();

	tit_addSearchActionInfo("profes:searchProfes0001ProfPaper", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioProfPaper=ds_oProfData" // outDs
		,"INSACODE=" + quote(ds_ioProfUser.getColumn(ds_ioProfUser.row, "INSACODE")) //-- 사번 // args 
		,"lfn_AfterSearchProfPaper"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 논문 및 연구실적 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchProfPaper(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdProfPaper); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    gdProfPaper.Redraw = true;
}

/*
 * 기능 : 논문 및 연구실적 저장
 * parameter : 없음
 * Return : 없음
 */
function lfn_SaveProfPaper(obj) {
	// if (!gfn_FormValidate(this)) { // 입력 Validation Check
        // return false;
    // }

	for (var j=0; j<ds_ioProfPaper.GetRowCount(); j++) {
		if (ds_ioProfPaper.GetRowType(j) == "insert") {
			ds_oProfPaperSingle.AddRow();
			ds_oProfPaperSingle.CopyRow(ds_oProfPaperSingle.row, ds_ioProfPaper, j);
			lfn_InsProfPaper(); // 논문 및 연구실적 입력
			ds_oProfPaperSingle.ClearData();
		}
    }

	tit_clearActionInfo();
    tit_AddSaveActionInfo(	
        "" // insert  		
        , "profes:updateProfes0001ProfPaper" // update  
        , "profes:deleteProfes0001ProfPaper" // delete
        , "" // flag column 명
        , "" // key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N" // 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iProfData=ds_ioProfPaper:U" // inDs		
        ,""	// outDs
        ,"PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") //-- 작성자사번 // args 
        ,"lfn_AfterSaveProfPaper"
        ,false
        ,false
		,true
    );
}

/*
 * 기능 : 논문 및 연구실적 저장 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveProfPaper(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

/*
 * 기능 : 논문 및 연구실적 입력
 * parameter : 없음
 * Return : 없음
 */
function lfn_InsProfPaper(obj) {
	tit_clearActionInfo();
	tit_AddSingleActionInfo("profes:insertProfes0001ProfPaper", "", 0, "", "N");
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iProfData=ds_oProfPaperSingle:A" // inDs		
		,""	// outDs
		,"PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") //-- 작성자사번 // args 
		,"lfn_AfterInsProfPaper"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 논문 및 연구실적 입력 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterInsProfPaper(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

