#include "LIB::profes.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    tit_createActionInfo(); // X-Framework용    
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
}

/*
 * 기능 : 그리드 정렬
 */
function gdProfPersonEdu_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1 && nCell > 0) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    gdProfPersonEdu.Editable = bStatus;
}

/*
 * 기능 : 교육훈련 조회
 * parameter : 없음
 * Return : 없음
 */
function lfn_SearchProfPersonEdu(obj) {
	gdProfPersonEdu.Redraw = false;
	
	ds_ioProfPersonEdu.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("profes:searchProfes0001ProfPersonEdu", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioProfPersonEdu=ds_oProfData" // outDs
		,"INSACODE=" + quote(ds_ioProfUser.getColumn(ds_ioProfUser.row, "INSACODE")) //-- 사번 // args 
		,"lfn_AfterSearchProfPersonEdu"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 교육훈련 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchProfPersonEdu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    radUseYn.Index = 0; // 허용 선택
    ds_ioProfPersonEdu.Filter("PEUSEPROFESSOR=='0'"); // 데이타셋 필터링
    gfn_SearchMessage(gdProfPersonEdu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    gdProfPersonEdu.Redraw = true;
}

/*
 * 기능 : 교육훈련 저장
 * parameter : 없음
 * Return : 없음
 */
function lfn_SaveProfPersonEdu(obj) {
	// if (!gfn_FormValidate(this)) { // 입력 Validation Check
        // return false;
    // }
	
	tit_clearActionInfo();
    tit_AddSaveActionInfo(	
        "" // insert  		
        , "profes:updateProfes0001ProfPersonEdu" // update  
        , "" // delete
        , "" // flag column 명
        , "" // key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N" // 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iProfData=ds_ioProfPersonEdu:U" // inDs		
        ,""	// outDs
        ,"PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") //-- 작성자사번 // args 
        ,"lfn_AfterSaveProfPersonLisence"
        ,false
        ,false
		,true
    );
}

/*
 * 기능 : 교육훈련 저장 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveProfPersonLisence(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

/*
 * 기능 : 전체보기
 */
function radUseYn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	switch (strCode) {
		case "Y":
			ds_ioProfPersonEdu.Filter("PEUSEPROFESSOR=='0'");
			break;
		case "A":
			ds_ioProfPersonEdu.UnFilter();
			break;
		default:
			break;
	}
}
