#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
    
    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y"); // 지부 코드 조회
    cbxJibu.Index = 0;
    lfn_SearchTeachingTimeMaster();	//시간표 유형 조회
    
    //lfn_Search();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oTimeSchedule.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0005TimeScheduleList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTimeSchedule=ds_oTimeSchedule"	// outDs
		,"TSJIBU=" + quote(cbxJibu.Value) // 지부
		 + " TSTYPE=" + quote(cbxType.Value)	// 시간표 유형
		,"lfn_AfterSearchTimeScheduleList"
		,false
	);
}

/*
 * 기능 : 조회 (시간표유형 조회)
 */
function lfn_SearchTeachingTimeMaster() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0005TimeType", false);
	
	tit_callService(
		""
		,""
		,""	// inDs
		,"dsTimeType=dsTimeType"	// outDs
		,"TSGEMGNO=" + quote(cbxJibu.Value) // 지부
		,""
		,false
		,true
		,true
	);
}

