/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
    btnInput.Enable = bStatus; // 추가 버튼
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchProfessorList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdProfessorList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdProfessorList, 0); // 멀티 정렬을 실행한다.
}

function btnSelect_OnClick(obj)
{
    var arrArg = Array(6);
		arrArg[0] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "GUBUN"); // 교수구분
		arrArg[1] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "GUBUNCD"); // 교수구분코드
		arrArg[2] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "SABUN"); // 교수번호
		arrArg[3] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "NAME"); // 교수명
		arrArg[4] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "JIKWI"); // 직위명
		arrArg[5] = ds_oProfessorList.GetColumn(ds_oProfessorList.currow, "JIKWICD"); // 직위코드
		Close(arrArg);
}

function gdProfessorList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    btnSelect_OnClick(obj);
}

function edProfesName_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        lfn_Search();
	}
}

function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchTeachingTimeMaster();
}

function btnAdd_OnClick(obj)
{
	var rIdx = ds_oTimeSchedule.AddRow();
	
	ds_oTimeSchedule.SetColumn(rIdx, "TSGEMGNO", dsParentObj.GetColumn(dsParentObj.currow, "EDULMGNO"));		//지부코드
	ds_oTimeSchedule.SetColumn(rIdx, "TSTYPE", dsParentObj.GetColumn(dsParentObj.currow, "EDUGUBUN"));			//시간표유형
	ds_oTimeSchedule.SetColumn(rIdx, "TSSEQ", dsParentObj.GetColumn(dsParentObj.currow, "EDUMGNO"));			//시간표순번
	ds_oTimeSchedule.SetColumn(rIdx, "TSTYPESEQ", dsParentObj.GetColumn(dsParentObj.currow, "EDUGTMGNO"));		//시간표유형순번
	ds_oTimeSchedule.SetColumn(rIdx, "TSTIME", dsParentObj.GetColumn(dsParentObj.currow, "EDUGTMGNO"));			//강의교시
	ds_oTimeSchedule.SetColumn(rIdx, "TSSTARTTIME", dsParentObj.GetColumn(dsParentObj.currow, "EDUGTMGNO"));	//강의시작시간
	ds_oTimeSchedule.SetColumn(rIdx, "TSENDTIME", dsParentObj.GetColumn(dsParentObj.currow, "EDUGTMGNO"));		//강의종료시간
	ds_oTimeSchedule.SetColumn(rIdx, "TSREMARK", dsParentObj.GetColumn(dsParentObj.currow, "EDUGTMGNO"));		//시간표유형설명
	ds_oTimeSchedule.SetColumn(rIdx, "TSHOUR", rIdx+1);		//강의시간
}

function btnDel_OnClick(obj)
{
	if(ds_oTimeSchedule.currow+1 == ds_oTimeSchedule.GetRowCount()) {
		ds_oTimeSchedule.DeleteRow(ds_oTimeSchedule.currow);
	} else {
		gfn_Alert("마지막 일차 순서대로 행삭제가 가능합니다.");
	}
}
