#include "LIB::profes.js"; // 공통업무 관련

var G_AUTH;

function Form_OnLoadCompleted(obj)
{
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }    
    
    // 공통 코드 조회
    lfn_SearchProfesCourseCode();	//교수관리 직능 조회
    lfn_SearchProfesSubjectCode();	//교수관리 과목 조회
    lfn_FilterProfesCourseCode(cbxTpEduGubun.Value, ds_oCourse, "전체");	//교수관리 직능 필터링
    cbxCours.Index = 0;
    lfn_FilterProfesSubjectCode("-1", ds_oSubject, "전체");	//교수관리 과목 필터링
    cbxSubject.Index = 0;
    
    if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM" || gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "PM") {
        G_AUTH = "A"; //시스템관리자면 권한을 관리자로 한다.
        ms_FormAuth = "A";
    }
    
    //그리드의 해더가 관리자는 결재상태 , 일반사용자는 승인일자 
    if (ms_FormAuth == "A") {
        //grdList.SetCellProp("head",5,"text","결재상태");
    } else  {    
        //grdList.SetCellProp("head",5,"text","승인일자");
    }
    
    var strYear = left(GetDate(), 4);
    strYear = ToNumber(strYear);
    ds_Year.AddRow(); ds_Year.SetColumn(0, "CD", "");        ds_Year.SetColumn(0, "DATA", "전체");
    ds_Year.AddRow(); ds_Year.SetColumn(1, "CD", strYear-2); ds_Year.SetColumn(1, "DATA", strYear-2);
    ds_Year.AddRow(); ds_Year.SetColumn(2, "CD", strYear-1); ds_Year.SetColumn(2, "DATA", strYear-1);
    ds_Year.AddRow(); ds_Year.SetColumn(3, "CD", strYear);   ds_Year.SetColumn(3, "DATA", strYear); 
    ds_Year.AddRow(); ds_Year.SetColumn(4, "CD", strYear+1); ds_Year.SetColumn(4, "DATA", strYear+1);
    
    cbxYear.Value = strYear;
    
    cbxCours.Visible   = true;
    cbxSubject.Visible = true;
    edCours.Visible    = false;
    edSubject.Visible  = false;
}

/*
 * 기능 : 교수관리 직능 조회
