 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    taTextArea.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */

/* 사용자 이벤트 함수 */
function btnSelect_OnClick(obj)
{
	if( gfn_IsNull(trim(taTextArea.Text)) ) {
		gfn_Alert("수정요청 사유를 입력하십시오.");
		taTextArea.SetFocus();
		return;
	}
	
	var arrArg = Array(2);
		arrArg[0] = "OK"; // 취소
		arrArg[1] = trim(taTextArea.Text); // 사유
		Close(arrArg);
}

function btnCancel_OnClick(obj)
{
	var arrArg = Array(2);
		arrArg[0] = "NO"; // 취소
		arrArg[1] = trim(taTextArea.Text); // 사유
		Close(arrArg);
}
