#include "LIB::profes.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	calDateFr.Value = left(GetDate(), 4) + "0101";
	calDateTo.Value = left(GetDate(), 8);
    
//    Cbo_Jibu_Init(); 
	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	
	// 임시로 소스배포
	if(gds_oUserInfo.GetColumn(0, "PSABUN") == "20220952" || gds_oUserInfo.GetColumn(0, "PSABUN") == "20151114"
	|| gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "PM")
	{
		tab_profes.tab1.Grid0.Visible = true;
		tab_profes.tab1.gdList.Visible = false;
	}
	else {
		tab_profes.tab1.Grid0.Visible = false;
		tab_profes.tab1.gdList.Visible = true;
	}
	
	
    lfn_Search();    
   
}

function lfn_SetFormAuth(bStatus) {

}

function lfn_Search(obj)
{
	
	tit_clearActionInfo(this);
	
	//TAB_Training.TabIndex = 1; //조회버튼 선택시  나오게 한다.
		tit_addCsvSearchActionInfo("profes:searchProfes0013StatPerson", false);
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "STARTDATE", calDateFr.Value);	
	ds_SearchJogeon.setColumn(0, "ENDDATE", calDateTo.Value);			
	ds_SearchJogeon.setColumn(0, "JIKWI", cbxJikwi.Value);	
	ds_SearchJogeon.setColumn(0, "GUBUN", cbxGubun.Value);	
    				
	tit_callService(  
	                 ""
	               , ""  
	               , "ds_iProfesList=ds_SearchJogeon"//inDs 
	               , "ds_ProfesList=ds_oProfesList" //outDs
	               , ""
	               , "afterSearch"  //callBack
	               , false
	               ); 
}

