function Chart_Start(strCode)
{

    //-----------소숫점 자리수 표현------------------------------------------------------- 
    TAB_Profes.tab2.XChart1.Axis[0].LabelsFormat.Decimals = 0;	  //소수점 자리수 표현 
    TAB_Profes.tab2.XChart1.AxisX.Font.size="6";
    TAB_Profes.tab2.XChart1.Font.size="7";
    //TAB_Training.tab2.XChart1.AxisY.LabelsFormat.Format = "AxisFormat_Currency"; 
    TAB_Profes.tab2.XChart1.AxisY.LabelsFormat.Format = "AxisFormat_Number";
    //------------------------------------------------------------------------------------

	TAB_Profes.tab2.XChart1.OpenData("COD_Values", 1, 2);
	
	
	TAB_Profes.tab2.XChart1.BackColor = "white";
	TAB_Profes.tab2.XChart1.BorderColor = "white";

	

	//축에 이름을 넣는다.
	TAB_Profes.tab2.XChart1.NValues = 10;
	For(var j=0;j<10;j++)	{
		TAB_Profes.tab2.XChart1.Legend(j) = ds_ProfesList.GetColumn(j,"NAME");   
	}
	
	For(var j=0;j<10;j++)	{
		TAB_Profes.tab2.XChart1.Value(0, j) = ds_ProfesList.GetColumn(j,"CNT1");  //실무
		TAB_Profes.tab2.XChart1.Value(1, j) = ds_ProfesList.GetColumn(j,"CNT2");  //강습
	}
    
	TAB_Profes.tab2.XChart1.DataEditor = True  ;
	TAB_Profes.tab2.XChart1.DataEditorObj.Docked = "Docked_Bottom"; 
	
	TAB_Profes.tab2.XChart1.SerLeg(0) = "실무"  ;
	TAB_Profes.tab2.XChart1.SerLeg(1) = "강습" ;

}


