function btnToExcel_OnClick(obj)
{

    if (ds_ProfesList.GetRowCount() < 1) {

        gfn_Alert("출력할 데이터가 존재하지 않습니다.");

        return false;

    }
    
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1503";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ProfesList.GetRowCount();
		parValue += " sTitle=" + quote(TAB_Profes.tab1.gdList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	
	//TAB_Profes.tab1.gdList.ExportExcel("개인별교육현황");
	fn_Excel(obj);

}

function fn_Excel(obj)
{
	ext_OleServerBusyTimeOut(true,5000,false,false);
    var mobj_Component = null;
    var marr_Grid = Array();

    var i = 0;
    for (var b = 0; b < Length(this.TAB_Profes.tab1.Components); b++) {
        mobj_Component = this.TAB_Profes.tab1.components(b);
        if (mobj_Component.GetType() == "Grid") {
            if (Length(mobj_Component.UserData) > 0) {
                marr_Grid[i] = mobj_Component;
                i++;
            }
        }
    }

    if (Length(marr_Grid) == 0) {
        return false;
    }

    var mobj_Export;
    mobj_Export = CreateExportObject();
    mobj_Export.ExportType = "Excel";
    mobj_Export.ExportFileName = this.Title + ".xls";
    mobj_Export.ActiveSheetName = marr_Grid[0].UserData;
    mobj_Export.MakeEmptyFileWhenNotExist = false;

    for (var j = 0; j < Length(marr_Grid); j++) {
        mobj_Export.AddExportGrid(marr_Grid[j].UserData + "!" + "A1", marr_Grid[j], true, false);
    }

    mobj_Export.Export();
    mobj_Export.Close();
    mobj_Export = null;	
}
