/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
    
    // 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y"); // 지부 코드 조회
    gfn_SearchAllJibu(cbxIJibu, "Y"); // 지부 코드 조회
    //cbxJibu.Index = 0;
//    cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD"); 
    lfn_SearchTeachingTimeType();	//시간표 유형 조회  
    //그리드의 값 초기화
//	cbxIJibu.Value = "";
	edTimeType.Text = "";
	cbxTotalTeachingTime.Value = "";
	radLunchYn.Value = "";
	cbxLunchTime.Value = "";
	tmTeachingTimeStart.Text = "";
	edTeachingTime.Text = "";
	edBreakTime.Text = "";

	//활성화
	var G_AUTH = gds_oUserInfo.GetColumn(0, "ADMINGUBUN");
	if (G_AUTH == "SM" || G_AUTH == "PM" ) {
	    cbxJibu.Enable = true;
	}else{
	    cbxJibu.Enable = false;
	}

}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oTimeSchedule.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0021TimeScheduleList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTimeSchedule=ds_oTimeSchedule"	// outDs
		,"TSJIBU=" + quote(cbxJibu.Value) // 지부
		 + " TSTYPE=" + quote(cbxType.Value)	// 시간표 유형
		,""
		,false
	);
}

/*
 * 기능 : 조회 (시간표유형 조회)
 */
function lfn_SearchTeachingTimeType() {
	ds_oTimeType.ClearData();
	
	tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0021TimeType", false);
	
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTimeType=ds_oTimeType"	// outDs
		,"TSGEMGNO=" + quote(cbxJibu.Value) // 지부
		,"lfn_AfterSearchTeachingTimeType"
		,false
		,true
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTeachingTimeType(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    if(ds_oTimeType.GetRowCount() > 0) {		
		ds_oTimeType.InsertRow(0);
		ds_oTimeType.SetColumn(0, "CD", "");
		ds_oTimeType.SetColumn(0, "DATA", "전체");
	}
	cbxType.Index = 0;
}

/*
 * 기능 : 입력
 */
function lfn_Input(obj)
{	
	//활성화
	var G_AUTH = gds_oUserInfo.GetColumn(0, "ADMINGUBUN");
	if (G_AUTH == "SM" || G_AUTH == "PM" ) {
	    cbxIJibu.Enable = true;
	}
	
	edTimeType.Enable = true;
	edTimeTypeNm.Enable = true;
	cbxTotalTeachingTime.Enable = true;
	radLunchYn.Enable = true;
	cbxLunchTime.Enable = true;
	tmTeachingTimeStart.Enable = true;
	edTeachingTime.Enable = true;
	edBreakTime.Enable = true;
	edRemark.Enable = true;	
	btnSave.Enable = true;
	cbxEduGubun.Enable = true;
	
	//Visible
	lbLunchTime.Visible = true;
	cbxLunchTime.Visible = true;
	
	//초기값 셋팅
	//cbxIJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");	//지부

	cbxEduGubun.Value = "T";    //교육구분
	edTimeType.Text = "";	//시간표유형
	edTimeTypeNm.Text = "";	//시간표유형이름
	cbxTotalTeachingTime.Index = 7;	//강의교시 : 8교시
	radLunchYn.Value = 1;	//점심시간 사용
	cbxLunchTime.Index = 3;	//점심시간 : 4교시
	tmTeachingTimeStart.Text = "0900";	//강의시작시간
	edTeachingTime.Text = "50";	//강의시간
	edBreakTime.Text = "10";	//쉬는시간
	edRemark.Text = "";	//비고
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj)
{
    
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    
    if(edTimeType.Enable == false) {
		gfn_Alert(MSGI00040+"\n추가버튼을 선택하여 [시간표 상세정보] 입력완료 후 저장 버튼을 누르세요.");
		return;
    }
    
    if(radLunchYn.Value = 1 && gfn_IsNull(cbxLunchTime.Value)) {
		gfn_Alert("점심시간 사용시에 점심시간 교시 입력은 필수항목입니다.");
		cbxLunchTime.SetFocus();
		return;
    }
    
    lfn_CreateDetailTimeScheduleView();
    lfn_SendDatailTimeSchedule();
}

