function ds_oTimeSchedule_OnRowPosChanged(obj,nOldRow,nRow)
{
	//비활성화
	cbxIJibu.Enable = false;
	edTimeType.Enable = false;
	edTimeTypeNm.Enable = false;
	cbxTotalTeachingTime.Enable = false;
	radLunchYn.Enable = false;
	cbxLunchTime.Enable = false;
	tmTeachingTimeStart.Enable = false;
	edTeachingTime.Enable = false;
	edBreakTime.Enable = false;
	edRemark.Enable = false;
	btnSave.Enable = false;
	cbxEduGubun.Enable = false;
	
	if(ds_oTimeSchedule.GetColumn(nRow, "LUNCHTIMEYN") == "1") {
		lbLunchTime.Visible = true;
		cbxLunchTime.Visible = true;
	} else {
		lbLunchTime.Visible = false;
		cbxLunchTime.Visible = false;
	}
	//그리드의 값 셋팅
	cbxIJibu.Value = ds_oTimeSchedule.GetColumn(nRow, "TSGEMGNO");
	cbxEduGubun.Value = ds_oTimeSchedule.GetColumn(nRow, "TSGUBUN");
	edTimeType.Text = trim(ds_oTimeSchedule.GetColumn(nRow, "TSTYPE"));
	edTimeTypeNm.Text = trim(ds_oTimeSchedule.GetColumn(nRow, "TSTYPENM"));
	cbxTotalTeachingTime.Value = ds_oTimeSchedule.GetColumn(nRow, "TSHOUR");
	radLunchYn.Value = ds_oTimeSchedule.GetColumn(nRow, "LUNCHTIMEYN");
	cbxLunchTime.Value = ds_oTimeSchedule.GetColumn(nRow, "LUNCHTIME");
	tmTeachingTimeStart.Text = ds_oTimeSchedule.GetColumn(nRow, "TSSTARTTIME");
	if(gfn_IsNull(trim(ds_oTimeSchedule.GetColumn(nRow, "TSTYPENM")))) {
		edTeachingTime.Text = ""; //ds_oTimeSchedule.GetColumn(nRow, "TSHOUR");
		edBreakTime.Text = ""; //ds_oTimeSchedule.GetColumn(nRow, "TSHOUR");
	} else {
		edTeachingTime.Text = "50"; //ds_oTimeSchedule.GetColumn(nRow, "TSHOUR");
		edBreakTime.Text = "10"; //ds_oTimeSchedule.GetColumn(nRow, "TSHOUR");
	}
	edRemark.Text = ds_oTimeSchedule.GetColumn(nRow, "TSREMARK");
}

/*
 * 기능 : 교육시작시간, 강의시간, 쉬는시간을 입력받아 교육시작시간, 교육종료시간, 다음교육시작시간을 구한다.
 * parameter : time1 -> 교육시작시간
 *			 : time2 -> 강의시간
 *			 : time3 -> 쉬는시간
 * return : arrTime[0] -> 교육시작시간
 *		  : arrTime[1] -> 교육종료시간
 *		  : arrTime[2] -> 다음교육시작시간
 */
function lfn_CalculatingTime(time1, time2, time3) {

	var sStartTimeHour = toNumber(left(time1,2));		//시(시작시간)
	var sStartTimeMinute = toNumber(right(time1,2));	//분(시작시간)
	
	var sEndTime = lfn_AddMinute(sStartTimeMinute, time2);	//교육종료시간
	var sNextTime = lfn_AddMinute(sStartTimeMinute, toNumber(time2)+toNumber(time3));	//다음교육시작시간
	
	var sEndTimeHour = sStartTimeHour + sEndTime[0];	//시(교육종료시간)
	var sEndTimeMinute = sEndTime[1];					//분(교육종료시간)
	
	var sNextTimeHour = sStartTimeHour + sNextTime[0];	//시(다음교육시작시간)
	var sNextTimeMinute = sNextTime[1];					//분(다음교육시작시간)
	
	var arrTime = Array(3);
	arrTime[0] = time1;		//교육시작시간
	arrTime[1] = lpad(toString(sEndTimeHour), "0", 2)+lpad(toString(sEndTimeMinute), "0", 2);		//교육종료시간
	arrTime[2] = lpad(toString(sNextTimeHour), "0", 2)+lpad(toString(sNextTimeMinute), "0", 2);		//다음교육시작시간
	
	return arrTime;
}

/*
 * 기능 : 시간(분+분)
 * parameter : min1 : 분
 *			 : min2 : 분
 * return : arrTime[0] : 시
 *		  : arrTime[1] : 분
 */
function lfn_AddMinute(min1, min2) {
	var arrTime = Array(2);
	
	if(toNumber(min1)+toNumber(min2) == 60) {
		arrTime[0] = 1;
		arrTime[1] = 0;
	} else if(toNumber(min1)+toNumber(min2) > 60) {
		arrTime[0] = floor((toNumber(min1)+toNumber(min2))/60);
		arrTime[1] = (toNumber(min1)+toNumber(min2))%60;
	} else if(toNumber(min1)+toNumber(min2) < 60) {
		arrTime[0] = 0;
		arrTime[1] = (toNumber(min1)+toNumber(min2))%60;
	}
	return arrTime;
}

function lfn_CreateDetailTimeScheduleView() {
	ds_iTimeScheduleDetailList.ClearData();	
	
	var sTimeCount = cbxTotalTeachingTime.Value;
	var sStartTime = tmTeachingTimeStart.Text;
	var sTeachingTime = edTeachingTime.Text;
	var sBreakTime = edBreakTime.Text;
	
	var arrTime = Array(2);
	
	if(radLunchYn.Value == 1) {
		sTimeCount++;
	}
	
	for(var i=0; i<sTimeCount; i++) {
		var nRow = ds_iTimeScheduleDetailList.AddRow();
		if(i==0) {
			if(radLunchYn.Value == 1) {	//점심시간이 있을경우
				if(cbxLunchTime.Value == (i+1)) {	//점심시간
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", 0);
				} else if(cbxLunchTime.Value < (i+1)) { //점심시간 이후 교시
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i);
				} else {	//점심시간 이전 교시
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i+1);
				}
			} else {	//점심시간이 없을경우
				ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i+1);
			}
			arrTime = lfn_CalculatingTime(sStartTime, sTeachingTime, sBreakTime);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSGEMGNO", cbxIJibu.Value);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSGUBUN", cbxEduGubun.Value);
			//ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPE", edTimeType.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPENM", edTimeTypeNm.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSSEQ", i+1);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPESEQ", "");
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSSTARTTIME", replace(sStartTime, ":", ""));
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSENDTIME", arrTime[1]);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSREMARK", edRemark.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSHOUR", floor((toNumber(sTeachingTime)+toNumber(sBreakTime))/60));
		} else {
			if(radLunchYn.Value == 1) {	//점심시간이 있을경우
				if(cbxLunchTime.Value == (i+1)) {	//점심시간
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", 0);
				} else if(cbxLunchTime.Value < (i+1)) { //점심시간 이후 교시
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i);
				} else {	//점심시간 이전 교시
					ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i+1);
				}
			} else {	//점심시간이 없을경우
				ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTIME", i+1);
			}
			arrTime = lfn_CalculatingTime(arrTime[2], sTeachingTime, sBreakTime);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSGEMGNO", cbxIJibu.Value);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSGUBUN", cbxEduGubun.Value);
			//ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPE", edTimeType.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPENM", edTimeTypeNm.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSSEQ", i+1);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSTYPESEQ", "");
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSSTARTTIME", arrTime[0]);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSENDTIME", arrTime[1]);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSREMARK", edRemark.Text);
			ds_iTimeScheduleDetailList.SetColumn(nRow, "TSHOUR", floor((toNumber(sTeachingTime)+toNumber(sBreakTime))/60));
		}
	}
}

