function cbxTotalTeachingTime_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var LunchTimeTmp = cbxLunchTime.Value;
	
	ds_oLunchTime.ClearData();
		
	for(var i=0; i<toNumber(strCode); i++) {
		ds_oLunchTime.AddRow();
		ds_oLunchTime.SetColumn(i, "CD", i+1);
		ds_oLunchTime.SetColumn(i, "DATA", (i+1)+"교시");
	}
	
	if(ds_oLunchTime.FindRow("CD", LunchTimeTmp) >= 0) {
		cbxLunchTime.Index = ds_oLunchTime.FindRow("CD", LunchTimeTmp);
	} else {
		cbxLunchTime.Index = 3;
	}
	
	if(toNumber(strCode) < 4) {
		radLunchYn.Index = 0;
		lbLunchTime.Visible = false;
		cbxLunchTime.Visible = false;
	} else {
		radLunchYn.Index = 1;
		lbLunchTime.Visible = true;
		cbxLunchTime.Visible = true;
	}
}

function edTeachingTime_OnKillFocus(obj)
{
	gfn_ComponentOnKillFocus(obj);

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 2));
    
    if(toNumber(edTeachingTime.Text) > 60) {
		gfn_Alert("분 단위는 0 ~ 60 사이로 입력하십시오.");
		edTeachingTime.Text = "50";
    }
}
function edBreakTime_OnKillFocus(obj)
{
	gfn_ComponentOnKillFocus(obj);

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 2));
    
    if(toNumber(edBreakTime.Text) > 60) {
		gfn_Alert("분 단위는 0 ~ 60 사이로 입력하십시오.");
		edBreakTime.Text = "10";
    }
}

function gdTimeSchedule_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sArg  = "sJibu="+quote(cbxIJibu.Value);
        sArg += " sGubun="+quote(cbxEduGubun.Value);
