Window.Script:
/*

 * 프로그램명 : frmPFM0005MLecHistory.xml

 * 설명 : 강의실적 등록 및 관리 (2010년 이전 버전)

 * 작성일 : 2010.01.08

 * 작성자 : 김은규

 */



#include "LIB::profes.js"; // 공통업무 관련



/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj)

{

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리



    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";



    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.



    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정

        lfn_SetFormAuth(true); // 저장 권한

    } else if (ms_FormAuth == "R") {

        lfn_SetFormAuth(false); // 읽기 권한

    } else {

        gfn_Alert(MSGI00080);

        close();

    }

    

    // 공통 코드 조회

    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회

    lfn_SearchProfesCourseCode();	//교수관리 직능 조회

    lfn_FilterProfesCourseCode(cbxGubun.Value, ds_oCourse, "전체");	//교수관리 직능 필터링

    cbxCourse.Index = 0;

    gfn_SearchCommonCode(ds_oCourseGubun, "COURSEGUBUN", "Y", "");    

    

    calDateStart.Value = "20090101";

    calDateEnd.Value = "20091231";

    

    /*

     * 강의실적에서 링크된 경우

     * sJibu : 지부

     * sEduGubun : 교육구분

     * sCourse : 교육과정

     * sOrder : 회차

     * sStatus : 상태

     * sDateStart : 교육시작일

     * sDateEnd : 교육종료일

     * sChkEduUnion : 통합강의

     * sChkStudy : 교육시간이 틀린강의

     */

     

    if (IsExistVar("sJibu") || IsExistVar("sEduGubun") || IsExistVar("sCourse") || IsExistVar("sOrder") || IsExistVar("sStatus")

		|| IsExistVar("sDateStart") || IsExistVar("sDateEnd") || IsExistVar("sChkEduUnion") || IsExistVar("sChkStudy")) {

		cbxJibu.Value = sJibu;

		cbxGubun.Value = sEduGubun;

		cbxCourse.Value = sCourse;

		edTrainingOrder.Text = sOrder;

		cbxStatus.Value = sStatus;

		calDateStart.Value = sDateStart;

		calDateEnd.Value = sDateEnd;

		chkEduUnion.Value = sChkEduUnion;

		chkStudy.Value = sChkStudy;

		

		if(left(calDateEnd.Value,4) > 2009) calDateEnd.Value = "20091231";

		

		lfn_Search();

	}

}



/*

 * 기능 : 조회

 */

function lfn_Search(obj) {



	if(calDateStart.Value > calDateEnd.Value) {

		gfn_Alert("시작일이 종료일보다 큽니다.\n다시 입력하세요.");

		return;

	}

	

	if(left(calDateStart.Value,4) > 2009 || left(calDateEnd.Value,4) > 2009) {

		gfn_Alert("2010년 이전 강의실적만 조회가능합니다.");

	}

	

    lfn_SearchOldLecture(obj);

}



/*

 * 기능 : 교수관리 직능 조회

 */

function lfn_SearchProfesCourseCode(obj) {

	

	tit_clearActionInfo();

	tit_addCsvSearchActionInfo("profes:searchProfesCourseCode", false);

	

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_oAllCourse=ds_oPFCourse"	// outDs

		,""	// args

		,""

		,true

		,true

		,true

	);

}



/*

 * 기능 : 교수관리 직능 필터링

 * parameter : sSearchGubun - 조회구분(그룹직능코드)

 *             objDataset - 리턴받을 데이타셋

 *             sOpt - "전체" 또는 "선택" 또는 " " 포함여부

 */

function lfn_FilterProfesCourseCode(sSearchGubun, objDataset, sOpt) {

	objDataset.ClearData();



    var nRow = -1;

    var ls_FilterExpr = "";

    if (!gfn_IsNull(sSearchGubun)) {

        ls_FilterExpr += "PCTCSEARCHGUBUN=='" + trim(sSearchGubun) + "'";

    }

    

	ds_oAllCourse.Filter(ls_FilterExpr);

	

    for (var i = 0; i < ds_oAllCourse.GetCount(); i++) {

        nRow = objDataset.AddRow();

        objDataset.SetColumn(nRow, "CD", ds_oAllCourse.GetColumn(i, "CD"));

        objDataset.SetColumn(nRow, "DATA", ds_oAllCourse.GetColumn(i, "DATA"));

        objDataset.SetColumn(nRow, "PCMGNO", ds_oAllCourse.GetColumn(i, "PCMGNO"));

		objDataset.SetColumn(nRow, "PCTCMUNJEGUBUN", ds_oAllCourse.GetColumn(i, "PCTCMUNJEGUBUN"));

		objDataset.SetColumn(nRow, "PCTCSEARCHGUBUN", ds_oAllCourse.GetColumn(i, "PCTCSEARCHGUBUN"));

    }



    if (length(sOpt) > 0) {

        objDataset.InsertRow(0);

        objDataset.SetColumn(0, "CD", "");

        objDataset.SetColumn(0, "DATA", sOpt);

        objDataset.SetColumn(0, "PCMGNO", "");

		objDataset.SetColumn(0, "PCTCMUNJEGUBUN", "");

		objDataset.SetColumn(0, "PCTCSEARCHGUBUN", "");

    }

    objDataset.row = 0;



    ds_oAllCourse.UnFilter();

}



/*--------------------------------------------------------

 *  교육구분 콤보가 바뀔경우 교육과정 콤보를 체운다.

 *--------------------------------------------------------*/

function cbxGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

    //실무교육을 선택하면 회차를 선택할 수 없도록 함.

    if (strText =="실무교육") {

        edTrainingOrder.Text = "";

        edTrainingOrder.Enable= false;

    } else {

        edTrainingOrder.Enable= true;

    }

    

	lfn_FilterProfesCourseCode(cbxGubun.Value, ds_oCourse, "전체");

	cbxCourse.Index = 0;

}



/*

 * 기능 : 조회 (교육과정)

 */

function lfn_SearchOldLecture(obj) {



	tit_clearActionInfo();

	tit_addCsvSearchActionInfo("profes:searchProfes0022OldLectureList", false);

	

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_oLecture=ds_oLecture"	// outDs

		,"EDUGTMGNO=" + quote(cbxJibu.Value) 					// 지부

        + " EDUGUBUN=" + quote(cbxGubun.Value)					// 교육구분

        + " EDUCCOURSECD=" + quote(cbxCourse.Value)				// 교육과정

        + " EDUMUNJEGUBUN=" + quote(ds_oCourse.GetColumn(ds_oCourse.FindRow("DATA", cbxCourse.Text), "PCTCMUNJEGUBUN"))	// 면제구분

        + " EDUORDER=" + quote(edTrainingOrder.Text)			// 회차

        + " EDULSTATUS=" + quote(cbxStatus.Value)				// 상태

        + " EDUSTARTDT=" + quote(left(calDateStart.Value,8))	// 교육일 FROM

        + " EDUENDDT=" + quote(left(calDateEnd.Value,8))        // 교육일 TO

        + " EDUUNION=" + quote(chkEduUnion.Value)        		// 통합교육

        + " EDUSTUDY=" + quote(chkStudy.Value)   				// 교육시간이 틀린강의실적

		,"lfn_AfterSearchOldLecture"

		,true

	);

}





/*

 * 기능 : 조회 후 처리해야 할 내용

 * parameter : nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchOldLecture(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    if(ds_oLecture.rowcount > 0) {

		if(ds_oLecture.GetColumn(0, "EDUUNIONYN") == "Y") {

			div1.Visible = false;

			div1.Visible = true;

		} else {

			div1.Visible = false;

			lfn_SearchOldLectureProfessor(0);

		}

    }

    gfn_SearchMessage(gdLecture); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

}



/*

 * 기능 : 조회 (강의실적)

 */

function ds_oLecture_OnRowPosChanged(obj,nOldRow,nRow)

{

	if(ds_oLecture.GetColumn(nRow, "EDUUNIONYN") == "Y") {

		div1.Visible = false;

		div1.Visible = true;

	} else {

		div1.Visible = false;

		lfn_SearchOldLectureProfessor(nRow);

	}

}


/*

 * 기능 : 조회 (강의실적)

 */

function lfn_SearchOldLectureProfessor(nRow) {



	tit_clearActionInfo();

	tit_addSearchActionInfo("profes:searchProfes0022OldLectureProfessorList", false);

	

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_oLectureProfessor=ds_oLectureProfessor"	// outDs

		,"LPTTLMGNO=" + quote(ds_oLecture.GetColumn(nRow, "EDULMGNO"))	//강의실적관리번호

		,"lfn_AfterSearchOldLectureProfessor"

		,true

	);

}



/*

 * 기능 : 조회 후 처리해야 할 내용

 * parameter : nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchOldLectureProfessor(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    gfn_SearchMessage(gdLectureProfessor); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

    

}



