function gdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)  
{
    var sArg = "";
    sArg += " name=" + ds_ProfesList.GetColumn(nRow,"LPPFNM");
    sArg += " jikwi=" + quote(ds_ProfesList.GetColumn(nRow,"LPPFJIKWINM"));    
    sArg += " gubun=" + quote(ds_ProfesList.GetColumn(nRow,"LPPFGUBUN"));    
    sArg += " datefr=" + calDateFr.Text;
    sArg += " dateto=" + calDateTo.Text; 
    //조회조건
    sArg += " sabun_Val=" + quote(ds_ProfesList.GetColumn(nRow,"LPPFSABUN"));    
    sArg += " datefr_Val=" + quote(calDateFr.Value);
    sArg += " dateto_Val=" + quote(calDateTo.Value); 
    
    
    //alert(sArg);
    
    Dialog("profes::frmprofesPopup0001PersonLecturing.xml", sArg, 600, 400, "", -1, -1);
    	
}

